import { useEffect, useState } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { useQuery } from "@tanstack/react-query";
import { isUnauthorizedError } from "@/lib/authUtils";
import Sidebar from "@/components/Sidebar";
import FloatingChatbot from "@/components/FloatingChatbot";
import LeadTable from "@/components/LeadTable";
import LeadDetailsModal from "@/components/LeadDetailsModal";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Search, Filter } from "lucide-react";

export default function Leads() {
  const { toast } = useToast();
  const { isAuthenticated, isLoading, user } = useAuth();
  const [selectedLead, setSelectedLead] = useState<any>(null);
  const [showLeadDetails, setShowLeadDetails] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState(() => {
    if (typeof window !== 'undefined') {
      return localStorage.getItem("selectedCategory") || "Client Hiring";
    }
    return "Client Hiring";
  });
  const [filters, setFilters] = useState({
    status: 'all',
    hrId: 'all',
    search: '',
    page: 1,
    limit: 20
  });

  useEffect(() => {
    const handleStorageChange = () => {
      const newCategory = localStorage.getItem("selectedCategory") || "Client Hiring";
      setSelectedCategory(newCategory);
    };

    window.addEventListener('storage', handleStorageChange);
    return () => window.removeEventListener('storage', handleStorageChange);
  }, []);

  // Show loading while authentication is being checked
  if (isLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
      </div>
    );
  }

  // Only redirect if definitely not authenticated
  if (!isAuthenticated) {
    window.location.href = "/login";
    return null;
  }

  const { data: leadsData, isLoading: leadsLoading, refetch } = useQuery({
    queryKey: ["/api/leads", filters, selectedCategory, user?.role],
    queryFn: async () => {
      const params = new URLSearchParams();
      Object.entries(filters).forEach(([key, value]) => {
        if (value && !(key === 'hrId' && value === 'all') && !(key === 'status' && value === 'all')) {
          params.append(key, value.toString());
        }
      });

      if (user?.role === 'hr' || user?.role === 'accounts') {
        params.append('category', selectedCategory);
      }

      const url = `/api/leads?${params.toString()}`;
      const res = await fetch(url, {
        credentials: "include",
      });

      if (!res.ok) {
        throw new Error(`HTTP error! status: ${res.status}`);
      }

      return res.json();
    },
    retry: false,
  });

  const { data: hrUsers, error: hrUsersError } = useQuery({
    queryKey: ["/api/users", { role: 'hr' }],
    queryFn: async () => {
      console.log('Fetching HR users...');
      const url = `/api/users?role=hr`;
      const res = await fetch(url, {
        credentials: "include",
      });

      if (!res.ok) {
        console.error(`Failed to fetch HR users: ${res.status}`);
        throw new Error(`HTTP error! status: ${res.status}`);
      }

      const data = await res.json();
      console.log('HR users fetched:', data);
      return data;
    },
    retry: false,
  });

  // Debug logging
  console.log('Leads page render:', { 
    hrUsers, 
    hrUsersError, 
    leadsData, 
    filters 
  });

  const handleViewLead = (lead: any) => {
    setSelectedLead(lead);
    setShowLeadDetails(true);
  };

  const handleFilterChange = (key: string, value: string) => {
    setFilters(prev => ({ ...prev, [key]: value, page: 1 }));
  };

  const handleSearch = (search: string) => {
    setFilters(prev => ({ ...prev, search, page: 1 }));
  };

  const handlePageChange = (page: number) => {
    setFilters(prev => ({ ...prev, page }));
  };

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary"></div>
      </div>
    );
  }

  if (!isAuthenticated) {
    return null;
  }

  return (
    <div className="landing-background min-h-screen flex relative">
      {/* Decorative shapes */}
      <div className="absolute top-0 left-0 w-64 h-64 bg-gradient-to-br from-[#11754c]/40 to-transparent rounded-full blur-3xl pointer-events-none"></div>
      <div className="absolute top-1/3 right-0 w-80 h-80 bg-gradient-to-br from-[#04e284]/35 to-transparent rounded-full blur-3xl pointer-events-none"></div>
      <div className="absolute bottom-0 left-1/2 transform -translate-x-1/2 w-full h-40 bg-gradient-to-t from-[#11754c]/30 to-transparent skew-y-3 pointer-events-none"></div>

      <FloatingChatbot />
      <Sidebar />

      {/* Main Content */}
      <div className="flex-1 flex flex-col overflow-hidden relative z-10">
        {/* Header */}
        <header className="bg-card border-b border-border px-6 py-4">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-2xl font-bold text-foreground" data-testid="text-page-title">
                Lead Management
              </h1>
              <p className="text-sm text-muted-foreground">
                Manage and track all leads in the system
              </p>
            </div>
          </div>
        </header>

        {/* Filters */}
        <div className="bg-card border-b border-border px-6 py-4">
          <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-4">
            <div className="flex items-center space-x-3">
              <div className="relative">
                <Input
                  type="text"
                  placeholder="Search leads..."
                  value={filters.search}
                  onChange={(e) => handleSearch(e.target.value)}
                  className="w-64 pl-10"
                  data-testid="input-search-leads"
                />
                <Search className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
              </div>

              <Select value={filters.status} onValueChange={(value) => handleFilterChange('status', value)}>
                <SelectTrigger className="w-40" data-testid="select-status-filter">
                  <SelectValue placeholder="All Status" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Status</SelectItem>
                  <SelectItem value="new">New</SelectItem>
                  <SelectItem value="register">Register</SelectItem>
                  <SelectItem value="scheduled">Scheduled</SelectItem>
                  <SelectItem value="completed">Completed</SelectItem>
                  <SelectItem value="not_interested">Not Interested</SelectItem>
                  <SelectItem value="pending">Pending</SelectItem>
                  <SelectItem value="ready_for_class">Ready for Class</SelectItem>
                </SelectContent>
              </Select>

              {Array.isArray(hrUsers) ? (
                <Select value={filters.hrId} onValueChange={(value) => handleFilterChange('hrId', value)}>
                  <SelectTrigger className="w-40" data-testid="select-hr-filter">
                    <SelectValue placeholder="All HR" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All HR</SelectItem>
                    {hrUsers.filter(user => user && (user.id || user.userId)).map((user: any) => (
                      <SelectItem key={user.id || user.userId} value={String(user.id || user.userId)}>
                        {user.fullName || user.firstName || 'Unknown User'}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              ) : (
                <div className="w-40 p-2 border rounded text-sm text-muted-foreground" data-testid="select-hr-filter">
                  {hrUsersError ? 'Error loading HR users' : 'Loading HR users...'}
                </div>
              )}

              <Button variant="secondary" data-testid="button-advanced-filter">
                <Filter className="mr-2 h-4 w-4" />
                Advanced Filter
              </Button>
            </div>
          </div>
        </div>

        {/* Lead Table */}
        <main className="flex-1 overflow-y-auto p-6">
          <LeadTable
            data={leadsData}
            loading={leadsLoading}
            onViewLead={handleViewLead}
            onPageChange={handlePageChange}
            currentPage={filters.page}
            onRefresh={refetch}
          />
        </main>
      </div>

      {/* Modals */}
      {showLeadDetails && selectedLead && (
        <LeadDetailsModal
          lead={selectedLead}
          isOpen={showLeadDetails}
          onClose={() => {
            setShowLeadDetails(false);
            setSelectedLead(null);
          }}
          onUpdate={refetch}
        />
      )}
    </div>
  );
}

