client/src/components/ActivityWarningPop.content:
import { useProductivityTracker } from '@/hooks/useProductivityTracker';
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog";
import { AlertTriangle, MousePointer2, Keyboard, Timer } from 'lucide-react';

export function ActivityWarningPopup() {
  const { activeWarning, warningMessage, dismissWarning } = useProductivityTracker();

  if (!activeWarning) return null;

  const getIcon = () => {
    switch (activeWarning) {
      case 'mouseIdle':
        return <MousePointer2 className="h-12 w-12 text-yellow-500" />;
      case 'keyboardIdle':
        return <Keyboard className="h-12 w-12 text-yellow-500" />;
      case 'longKeyPress':
        return <Timer className="h-12 w-12 text-orange-500" />;
      default:
        return <AlertTriangle className="h-12 w-12 text-yellow-500" />;
    }
  };

  const getTitle = () => {
    switch (activeWarning) {
      case 'mouseIdle':
        return 'Mouse Inactive';
      case 'keyboardIdle':
        return 'Keyboard Inactive';
      case 'longKeyPress':
        return 'Long Key Press Detected';
      default:
        return 'Activity Warning';
    }
  };

  return (
    <AlertDialog open={!!activeWarning} onOpenChange={() => dismissWarning()}>
      <AlertDialogContent className="max-w-md">
        <AlertDialogHeader>
          <div className="flex flex-col items-center gap-4">
            <div className="p-4 bg-yellow-50 rounded-full animate-pulse">
              {getIcon()}
            </div>
            <AlertDialogTitle className="text-xl text-center">
              {getTitle()}
            </AlertDialogTitle>
          </div>
          <AlertDialogDescription className="text-center text-base mt-2">
            {warningMessage}
          </AlertDialogDescription>
        </AlertDialogHeader>
        <AlertDialogFooter className="sm:justify-center">
          <AlertDialogAction 
            onClick={dismissWarning}
            className="px-8"
          >
            I'm Active
          </AlertDialogAction>
        </AlertDialogFooter>
      </AlertDialogContent>
    </AlertDialog>
  );
}

