  }

  .metric-card {
    @apply bg-card border border-border rounded-lg p-6 transition-all duration-200 hover:shadow-lg;
  }

  .sidebar-item {
    @apply flex items-center px-4 py-2 text-sm font-medium rounded-lg transition-colors;
  }

  .sidebar-item:hover {
    @apply bg-accent text-accent-foreground;
  }

  .sidebar-item.active {
    @apply bg-primary text-primary-foreground;
  }

  .upload-area {
    @apply border-2 border-dashed border-border rounded-lg p-8 text-center bg-muted transition-colors hover:border-primary hover:bg-muted cursor-pointer;
    background-color: hsla(var(--muted) / 0.5);
  }

  .upload-area:hover {
    background-color: var(--muted);
  }

  .progress-bar {
    @apply w-full bg-secondary rounded-full h-2;
  }

  .progress-fill {
    @apply bg-primary h-2 rounded-full transition-all duration-300;
  }

  .chart-container {
    @apply bg-card border border-border rounded-lg p-6;
  }

  .card {
    @apply shadow-green-lg;
  }

  .card:hover {
    @apply shadow-green-xl;
  }

  @keyframes gradient-shift {
    0% {
      background-position: 0% 50%;
    }

    50% {
      background-position: 100% 50%;
    }

    100% {
      background-position: 0% 50%;
    }
  }

  @keyframes wave-animation {

    0%,
    100% {
      transform: translateY(0px);
    }

    50% {
      transform: translateY(-10px);
    }
  }

  @keyframes diagonal-slide {
    0% {
      background-position: 0% 0%;
    }

    100% {
      background-position: 100% 100%;
    }
  }

  .landing-background {
    background: linear-gradient(135deg,
        #F9F9F9 0%,
        #f5faf8 25%,
        #eef7f4 50%,
        #f5faf8 75%,
        #F9F9F9 100%);
    background-size: 400% 400%;
    animation: gradient-shift 15s ease infinite;
    position: relative;
    overflow: hidden;
  }

  .landing-background::before {
    content: '';
    position: absolute;
    top: -30%;
    left: 0;
    width: 150%;
    height: 80%;
    background: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1200 120"><path d="M0,60 Q300,30 600,60 T1200,60 L1200,0 L0,0 Z" fill="rgba(17,117,76,0.08)" /><path d="M0,80 Q300,50 600,80 T1200,80 L1200,120 L0,120 Z" fill="rgba(4,226,132,0.06)" /></svg>') repeat-x;
    background-size: 300% 100%;
    animation: wave-animation 6s ease-in-out infinite;
    pointer-events: none;
    opacity: 0.5;
  }

  .landing-background::after {
    content: '';
    position: absolute;
    bottom: 0;
    right: -10%;
    width: 600px;
    height: 600px;
    background: radial-gradient(circle at 50% 50%, rgba(4, 226, 132, 0.12) 0%, rgba(17, 117, 76, 0.05) 50%, transparent 100%);
    border-radius: 50%;
    animation: float-slow 25s ease-in-out infinite;
    pointer-events: none;
  }

  .landing-background.dark {
    background: linear-gradient(135deg,
        #0a0a0a 0%,
        #0d1f1a 25%,
        #0f2620 50%,
        #0d1f1a 75%,
        #0a0a0a 100%);
    background-size: 400% 400%;
    animation: gradient-shift 15s ease infinite;
  }

  .landing-background.dark::before {
    background: radial-gradient(circle at 20% 30%, rgba(4, 226, 132, 0.05) 0%, transparent 50%),
      radial-gradient(circle at 80% 70%, rgba(17, 117, 76, 0.05) 0%, transparent 50%);
  }

  @keyframes float-slow {

    0%,
    100% {
      transform: translateY(0px) rotate(0deg);
    }

    50% {
      transform: translateY(-20px) rotate(2deg);
    }
  }

  @keyframes pulse-glow {

    0%,
    100% {
      opacity: 0.5;
    }

    50% {
      opacity: 0.8;
    }
  }

  .portal-background {
    position: relative;
    background: linear-gradient(135deg, #F9F9F9 0%, #f5f9f7 50%, #F9F9F9 100%);
    min-height: 100vh;
  }

  .portal-background::before {
    content: '';
    position: fixed;
    top: -50%;
    right: -10%;
    width: 500px;
    height: 500px;
    background: radial-gradient(circle, rgba(4, 226, 132, 0.08) 0%, transparent 70%);
    border-radius: 50%;
    animation: float-slow 20s ease-in-out infinite;
    pointer-events: none;
    z-index: 0;
  }

  .portal-background::after {
    content: '';
    position: fixed;
    bottom: -20%;
    left: -5%;
    width: 400px;
    height: 400px;
    background: radial-gradient(circle, rgba(17, 117, 76, 0.08) 0%, transparent 70%);
    border-radius: 50%;
    animation: float-slow 25s ease-in-out infinite reverse;
    pointer-events: none;
    z-index: 0;
  }

  .portal-background.dark {
    background: linear-gradient(135deg, #0a0a0a 0%, #0d1515 50%, #0a0a0a 100%);
  }

  .portal-background.dark::before {
    background: radial-gradient(circle, rgba(4, 226, 132, 0.12) 0%, transparent 70%);
  }

  .portal-background.dark::after {
    background: radial-gradient(circle, rgba(17, 117, 76, 0.12) 0%, transparent 70%);
  }

  .portal-content {
    position: relative;
    z-index: 10;
  }

  .portal-card {
    background: rgba(255, 255, 255, 0.7);
    backdrop-filter: blur(10px);
    border: 1px solid rgba(17, 117, 76, 0.1);
    transition: all 0.3s ease;
  }

  .portal-card:hover {
    background: rgba(255, 255, 255, 0.9);
    border-color: rgba(4, 226, 132, 0.2);
    transform: translateY(-2px);
  }

  .dark .portal-card {
    background: rgba(26, 26, 26, 0.5);
    border: 1px solid rgba(4, 226, 132, 0.1);
  }

  .dark .portal-card:hover {
    background: rgba(26, 26, 26, 0.8);
    border-color: rgba(4, 226, 132, 0.3);
  }

  @keyframes slideInFromRight {
    from {
      opacity: 0;
      transform: translateX(100%);
    }

    to {
      opacity: 1;
      transform: translateX(0);
    }
  }

  @keyframes slideOutToLeft {
    from {
      opacity: 1;
      transform: translateX(0);
    }

    to {
      opacity: 0;
      transform: translateX(-100%);
    }
  }

  .slideshow-container {
    position: relative;
    width: 100%;
    height: 300px;
    overflow: hidden;
    border-radius: 8px;
  }

  .slide {
    position: absolute;
    width: 100%;
    height: 100%;
    opacity: 0;
    transition: opacity 0.5s ease-in-out;
  }

  .slide.active {
    opacity: 1;
    animation: slideInFromRight 0.8s ease-out;
  }

  .slide img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .slide-nav {
    position: absolute;
    bottom: 20px;
    left: 50%;
    transform: translateX(-50%);
    display: flex;
    gap: 8px;
    z-index: 10;
  }

  .slide-dot {
    width: 10px;
    height: 10px;
    border-radius: 50%;
    background: rgba(255, 255, 255, 0.5);
    cursor: pointer;
    transition: all 0.3s ease;
    border: 2px solid rgba(255, 255, 255, 0.3);
  }

  .slide-dot.active {
    background: #04e284;
    border-color: #04e284;
    transform: scale(1.2);
  }

  /* Dashboard Card Height Classes */
  .dashboard-allocation-card {
    height: 150px;
  }

  .dashboard-notification-send {
    height: 500px;
  }

  .dashboard-notification-receive {
    height: 600px;
  }
}

