import { useEffect, useMemo, useState } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { useQuery, useMutation } from "@tanstack/react-query";
import { isUnauthorizedError } from "@/lib/authUtils";
import Sidebar from "@/components/Sidebar";
import FloatingChatbot from "@/components/FloatingChatbot";
import MetricsCard from "@/components/MetricsCard";
import StatusChart from "@/components/StatusChart";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Progress } from "@/components/ui/progress";
import { Textarea } from "@/components/ui/textarea";
import {
  Users,
  Clock,
  CheckCircle,
  DollarSign,
  Upload,
  Search,
  Send,
  Bell,
  Download,
  Filter,
} from "lucide-react";
import BulkUploadModal from "@/components/BulkUploadModal";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Checkbox } from "@/components/ui/checkbox";
import { apiRequest, queryClient } from "@/lib/queryClient";

export default function Dashboard() {
  const { toast } = useToast();
  const { isAuthenticated, isLoading, user } = useAuth();
  const [showBulkUpload, setShowBulkUpload] = useState(false);
  const [isDownloading, setIsDownloading] = useState(false);
  const [showDatePicker, setShowDatePicker] = useState(false);
  const [fromDate, setFromDate] = useState("");
  const [toDate, setToDate] = useState("");
  const [dateRange, setDateRange] = useState<{
    minDate: string;
    maxDate: string;
  } | null>(null);
  const [notificationTitle, setNotificationTitle] = useState("");
  const [notificationMessage, setNotificationMessage] = useState("");
  const [selectedRoles, setSelectedRoles] = useState<string[]>(["hr"]);
  const [notificationImage, setNotificationImage] = useState<File | null>(null);
  const [imagePreview, setImagePreview] = useState<string>("");
  const [selectedCategory, setSelectedCategory] = useState(() => {
    if (typeof window !== 'undefined') {
      return localStorage.getItem("selectedCategory") || "Client Hiring";
    }
    return "Client Hiring";
  });

  const [managerCategoryFilter, setManagerCategoryFilter] = useState("all");

  const categoryOptions = [
    "Client Hiring",
    "Medical Coding", 
    "Talent Acquisition Executive",
    "Technical Hiring"
  ];

  // Listen for localStorage changes (when HR changes category on login screen)
  useEffect(() => {
    const handleStorageChange = () => {
      const newCategory = localStorage.getItem("selectedCategory") || "Client Hiring";
      setSelectedCategory(newCategory);
    };

    window.addEventListener('storage', handleStorageChange);
    return () => window.removeEventListener('storage', handleStorageChange);
  }, []);

  // Fetch date range on mount (only for accounts and admin)
  useEffect(() => {
    const fetchDateRange = async () => {
      try {
        // Only fetch if user is accounts or admin
        if (user && (user.role === "accounts" || user.role === "admin")) {
          const response = await fetch("/api/download-date-range", {
            credentials: 'include'
          });
          const data = await response.json();
          setDateRange(data);
          setFromDate(data.minDate);
          setToDate(data.maxDate);
        }
      } catch (error) {
        console.error("Error fetching date range:", error);
      }
    };
    fetchDateRange();
  }, [user]);

  const handleDownloadData = async () => {
    try {
      if (!fromDate || !toDate) {
        toast({
          title: "Error",
          description: "Please select both from and to dates",
          variant: "destructive",
        });
        return;
      }

      setIsDownloading(true);
      const params = new URLSearchParams({
        fromDate,
        toDate,
      });
      const response = await fetch(`/api/download-data?${params}`, {
        credentials: 'include'
      });
      if (!response.ok) {
        throw new Error("Failed to download data");
      }
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement("a");
      link.href = url;
      link.setAttribute("download", `MyData_${fromDate}_to_${toDate}.xlsx`);
      document.body.appendChild(link);
      link.click();
      link.parentNode?.removeChild(link);
      window.URL.revokeObjectURL(url);
      setShowDatePicker(false);
      toast({
        title: "Success",
        description: `Your data (${fromDate} to ${toDate}) has been downloaded`,
      });
    } catch (error) {
      console.error("Download error:", error);
      toast({
        title: "Error",
        description: "Failed to download your data. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsDownloading(false);
    }
  };

  const sendNotificationMutation = useMutation({
    mutationFn: async (data: {
      title: string;
      message: string;
      roles: string[];
      image?: File;
    }) => {
      const formData = new FormData();
      formData.append("title", data.title);
      formData.append("message", data.message);
      formData.append("roles", JSON.stringify(data.roles));
      if (data.image) {
        formData.append("image", data.image);
      }

      const response = await fetch("/api/notifications/send", {
        method: "POST",
        body: formData,
      });
      return response.json();
    },
    onSuccess: () => {
      setNotificationTitle("");
      setNotificationMessage("");
      setSelectedRoles(["hr"]);
      setNotificationImage(null);
      setImagePreview("");
      queryClient.invalidateQueries({ queryKey: ["/api/notifications"] });
      toast({
        title: "Notification Sent",
        description: "Message delivered to selected team members",
      });
    },
    onError: (error) => {
      toast({
        title: "Failed to Send",
        description: "Could not send notification. Please try again.",
        variant: "destructive",
      });
    },
  });

  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, toast]);

  // Determine which category to use for filtering
  const activeCategoryFilter = useMemo(() => {
    if (user?.role === 'manager' || user?.role === 'admin') {
      return managerCategoryFilter; // Manager can filter by category or see all
    }
    return selectedCategory; // HR/Accounts use their localStorage category
  }, [user?.role, managerCategoryFilter, selectedCategory]);

  const { data: metrics, isLoading: metricsLoading } = useQuery({
    queryKey: ["/api/metrics", activeCategoryFilter],
    queryFn: async () => {
      const params = new URLSearchParams();
      // Only add category filter if not "all"
      if (activeCategoryFilter !== 'all') {
        params.append('category', activeCategoryFilter);
      }
      const url = `/api/metrics?${params}`;
      const response = await fetch(url, { credentials: 'include' });
      if (!response.ok) throw new Error("Failed to fetch metrics");
      return response.json();
    },
    retry: false,
  });

  const { data: recentActivity } = useQuery({
    queryKey: ["/api/leads/recent"],
    retry: false,
  });

  const { data: leadsData } = useQuery({
    queryKey: ["/api/leads", activeCategoryFilter, user?.role],
    queryFn: async () => {
      const params = new URLSearchParams();
      // Only add category filter if not "all"
      if (activeCategoryFilter !== 'all') {
        params.append('category', activeCategoryFilter);
      }
      const url = `/api/leads?${params}`;
      const response = await fetch(url, { credentials: 'include' });
      if (!response.ok) throw new Error("Failed to fetch leads");
      return response.json();
    },
    retry: false,
    refetchInterval: 3000,
    refetchOnWindowFocus: true,
  });

  const { data: myLeadsData } = useQuery({
    queryKey: ["/api/my/leads", activeCategoryFilter, user?.role],
    queryFn: async () => {
      const params = new URLSearchParams();
      if (activeCategoryFilter !== 'all') {
        params.append('category', activeCategoryFilter);
      }
      const url = `/api/my/leads?${params}`;
      const response = await fetch(url, { credentials: 'include' });
      if (!response.ok) throw new Error("Failed to fetch leads");
      return response.json();
    },
    retry: false,
    refetchInterval: 3000,
    refetchOnWindowFocus: true,
  });

  const { data: myCompletedData } = useQuery({
    queryKey: ["/api/my/completed", activeCategoryFilter, user?.role],
    queryFn: async () => {
      const params = new URLSearchParams();
      if (activeCategoryFilter !== 'all') {
        params.append('category', activeCategoryFilter);
      }
      const url = `/api/my/completed?${params}`;
      const response = await fetch(url, { credentials: 'include' });
      if (!response.ok) throw new Error("Failed to fetch leads");
      return response.json();
    },
    retry: false,
    refetchInterval: 3000,
    refetchOnWindowFocus: true,
  });

  const { data: hrUsersData } = useQuery({
    queryKey: ["/api/users"],
    queryFn: async () => {
      const response = await fetch("/api/users?role=hr");
      if (!response.ok) throw new Error("Failed to fetch HR users");
      return response.json();
    },
    retry: false,
    refetchInterval: 5000,
    refetchOnWindowFocus: true,
  });

  const { data: leadHistoryData } = useQuery({
    queryKey: ["/api/history/all"],
    retry: false,
    refetchInterval: 3000,
    refetchOnWindowFocus: true,
  });

  const { data: notificationsData = [] } = useQuery({
    queryKey: ["/api/notifications"],
    retry: false,
    enabled: user?.role !== "manager",
    refetchInterval: 2000,
    refetchOnWindowFocus: true,
  });

  // Define all available statuses
  const ALL_STATUSES = [
    "new",
    "register",
    "scheduled",
    "completed",
    "not_interested",
    "pending",
    "ready_for_class",
    "wrong_number",
    "not_picking",
    "call_back",
  ];

  // Calculate role-specific status distribution for pie chart
  const roleSpecificMetrics = useMemo(() => {
    // For all roles: Show their own leads' status + completed leads
    const statusCounts: { [key: string]: number } = {};

    // Initialize all statuses with 0
    ALL_STATUSES.forEach((status) => {
      statusCounts[status] = 0;
    });

    // Count current leads by status
    if (myLeadsData?.leads && Array.isArray(myLeadsData.leads)) {
      myLeadsData.leads.forEach((lead: any) => {
        const status = lead.status || "new";
        statusCounts[status] = (statusCounts[status] || 0) + 1;
      });
    }

    // Add completed leads count
    if (myCompletedData?.leads && Array.isArray(myCompletedData.leads)) {
      statusCounts["completed"] =
        (statusCounts["completed"] || 0) + myCompletedData.leads.length;
    }

    // Filter out statuses with 0 count for cleaner display
    const filteredStatuses: { [key: string]: number } = {};
    Object.entries(statusCounts).forEach(([status, count]) => {
      if (count > 0) {
        filteredStatuses[status] = count;
      }
    });

    // If no personal data, return metrics distribution (for roles that don't have my/leads)
    if (Object.keys(filteredStatuses).length === 0) {
      return metrics?.statusDistribution || {};
    }

    return filteredStatuses;
  }, [
    metrics?.statusDistribution,
    myLeadsData,
    myCompletedData,
    user?.id,
    user?.role,
  ]);

  // Calculate total collected amount (registration + partial amounts)
  const collectedAmount = useMemo(() => {
    if (!leadsData?.leads || !Array.isArray(leadsData.leads)) return 0;
    return leadsData.leads.reduce((total, lead) => {
      const reg = lead.registrationAmount
        ? parseFloat(lead.registrationAmount)
        : 0;
      const partial = lead.partialAmount ? parseFloat(lead.partialAmount) : 0;
      return total + reg + partial;
    }, 0);
  }, [leadsData]);

  // Calculate HR status assignment breakdown
  const hrStatusAssignments = useMemo(() => {
    if (!hrUsersData || !leadsData?.leads) return [];

    const hrStatus: {
      [key: string]: {
        name: string;
        statuses: { [key: string]: number };
      };
    } = {};

    hrUsersData.forEach((hr: any) => {
      hrStatus[hr.id] = {
        name: `${hr.firstName} ${hr.lastName}`,
        statuses: {},
      };
    });

    // Count leads by status for each HR owner (current leads)
    if (Array.isArray(leadsData.leads)) {
      leadsData.leads.forEach((lead: any) => {
        if (lead.currentOwnerId && hrStatus[lead.currentOwnerId]) {
          const status = lead.status || "new";
          hrStatus[lead.currentOwnerId].statuses[status] =
            (hrStatus[lead.currentOwnerId].statuses[status] || 0) + 1;
        }
      });
    }

    // Count completed leads for each HR from history (leads they completed and passed to Accounts)
    if (Array.isArray(leadHistoryData)) {
      leadHistoryData.forEach((history: any) => {
        // Look for handoff entries where HR passed a completed lead to Accounts
        if (
          history.fromUserId &&
          hrStatus[history.fromUserId] &&
          history.changeReason &&
          history.changeReason.includes("completed")
        ) {
          if (!hrStatus[history.fromUserId].statuses["completed"]) {
            hrStatus[history.fromUserId].statuses["completed"] = 0;
          }
          hrStatus[history.fromUserId].statuses["completed"]++;
        }
      });
    }

    return Object.values(hrStatus)
      .filter((hr) => Object.keys(hr.statuses).length > 0)
      .sort((a, b) => {
        const aTotal = Object.values(a.statuses).reduce(
          (sum: number, count: number) => sum + count,
          0,
        );
        const bTotal = Object.values(b.statuses).reduce(
          (sum: number, count: number) => sum + count,
          0,
        );
        return bTotal - aTotal;
      })
      .slice(0, 10);
  }, [hrUsersData, leadsData, leadHistoryData]);

  // Format amount to display (e.g., 128000 -> "128K")
  const formatRevenueDisplay = (amount: number) => {
    if (amount >= 1000000) return `${(amount / 1000000).toFixed(1)}M`;
    if (amount >= 1000) return `${(amount / 1000).toFixed(0)}K`;
    return `${amount.toFixed(0)}`;
  };

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary"></div>
      </div>
    );
  }

  if (!isAuthenticated) {
    return null;
  }

  return (
    <div className="landing-background min-h-screen flex relative bg-gradient-to-br from-[#11754c]/8 via-[#F9F9F9] to-[#04e284]/5 dark:from-[#11754c]/10 dark:via-[#0a0a0a] dark:to-[#04e284]/8">
      {/* Decorative shapes */}
      <div className="absolute top-0 left-0 w-64 h-64 bg-gradient-to-br from-[#11754c]/60 to-transparent rounded-full blur-3xl pointer-events-none"></div>
      <div className="absolute top-1/3 right-0 w-80 h-80 bg-gradient-to-br from-[#04e284]/50 to-transparent rounded-full blur-3xl pointer-events-none"></div>
      <div className="absolute bottom-0 left-1/2 transform -translate-x-1/2 w-full h-40 bg-gradient-to-t from-[#11754c]/50 to-transparent skew-y-3 pointer-events-none"></div>

      <FloatingChatbot />
      <Sidebar />

      {/* Main Content */}
      <div className="flex-1 flex flex-col overflow-hidden relative z-10">
        {/* Header */}
        <header className="bg-card border-b border-border px-6 py-4">
          <div className="flex items-center justify-between">
            <div>
              <h1
                className="text-2xl font-bold text-foreground"
                data-testid="text-page-title"
              >
                Dashboard Overview
              </h1>
              <p className="text-sm text-muted-foreground">
                Monitor leads, manage users, and track performance
              </p>
            </div>
            <div className="flex items-center space-x-4">
              <div className="relative">
                <Input
                  type="text"
                  placeholder="Global search..."
                  className="w-64 pl-10"
                  data-testid="input-global-search"
                />
                <Search className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
              </div>

              {/* Date Range Picker - Only for accounts and admin */}
              {user && (user.role === "accounts" || user.role === "admin") && (
                <div className="relative">
                  <Button
                    onClick={() => setShowDatePicker(!showDatePicker)}
                    disabled={isDownloading}
                    variant="outline"
                    data-testid="button-download-data"
                  >
                    <Download className="mr-2 h-4 w-4" />
                    {isDownloading ? "Downloading..." : "Download Data"}
                  </Button>

                  {/* Dropdown for date selection */}
                  {showDatePicker && (
                    <div className="absolute right-0 mt-2 w-80 bg-white dark:bg-[#1a1a1a] border border-border rounded-lg shadow-lg z-50 p-4 space-y-3">
                      <div>
                        <label className="text-sm font-medium">From Date</label>
                        <Input
                          type="date"
                          value={fromDate}
                          onChange={(e) => setFromDate(e.target.value)}
                          min={dateRange?.minDate}
                          max={dateRange?.maxDate}
                          className="mt-1"
                          data-testid="input-from-date"
                        />
                      </div>
                      <div>
                        <label className="text-sm font-medium">To Date</label>
                        <Input
                          type="date"
                          value={toDate}
                          onChange={(e) => setToDate(e.target.value)}
                          min={dateRange?.minDate}
                          max={dateRange?.maxDate}
                          className="mt-1"
                          data-testid="input-to-date"
                        />
                      </div>
                      <div className="text-xs text-muted-foreground">
                        Data available from{" "}
                        <strong>{dateRange?.minDate}</strong> to{" "}
                        <strong>{dateRange?.maxDate}</strong>
                      </div>
                      <div className="flex gap-2">
                        <Button
                          onClick={handleDownloadData}
                          disabled={isDownloading}
                          size="sm"
                          className="flex-1"
                          data-testid="button-download-confirm"
                        >
                          {isDownloading ? "Downloading..." : "Download"}
                        </Button>
                        <Button
                          onClick={() => setShowDatePicker(false)}
                          size="sm"
                          variant="ghost"
                          className="flex-1"
                          data-testid="button-close-date-picker"
                        >
                          Cancel
                        </Button>
                      </div>
                    </div>
                  )}
                </div>
              )}

              {(user?.role === "manager" || user?.role === "admin") && (
                <div className="flex items-center gap-2">
                  <Filter className="h-4 w-4 text-muted-foreground" />
                  <Select
                    value={managerCategoryFilter}
                    onValueChange={setManagerCategoryFilter}
                  >
                    <SelectTrigger className="w-48" data-testid="select-category-filter">
                      <SelectValue placeholder="Filter by Category" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">All Categories</SelectItem>
                      {categoryOptions.map((cat) => (
                        <SelectItem key={cat} value={cat}>{cat}</SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>
              )}

              {(user?.role === "manager" || user?.role === "admin") && (
                <Button
                  onClick={() => setShowBulkUpload(true)}
                  data-testid="button-bulk-upload"
                >
                  <Upload className="mr-2 h-4 w-4" />
                  Bulk Upload
                </Button>
              )}
            </div>
          </div>
        </header>

        {/* Dashboard Content */}
        <main className="flex-1 overflow-y-auto p-6">
          {/* Category indicator for HR users */}
          {(user?.role === "hr" || user?.role === "accounts") && (
            <div className="mb-4 flex items-center gap-2 text-sm text-muted-foreground">
              <Filter className="h-4 w-4" />
              <span>Showing data for category: <strong className="text-foreground">{selectedCategory}</strong></span>
            </div>
          )}

          {/* Category indicator for managers when filtering */}
          {(user?.role === "manager" || user?.role === "admin") && managerCategoryFilter !== "all" && (
            <div className="mb-4 flex items-center gap-2 text-sm text-muted-foreground">
              <Filter className="h-4 w-4" />
              <span>Filtering by category: <strong className="text-foreground">{managerCategoryFilter}</strong></span>
            </div>
          )}

          {/* Metrics Grid */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
            <MetricsCard
              title={user?.role === "hr" || user?.role === "accounts" ? "My Leads" : "Total Leads"}
              value={
                user?.role === "hr" || user?.role === "accounts"
                  ? (myLeadsData?.leads?.length || 0)
                  : (leadsData?.leads?.length || 0)
              }
              icon={Users}
              change={user?.role === "hr" || user?.role === "accounts" ? selectedCategory : (managerCategoryFilter !== "all" ? managerCategoryFilter : "All Categories")}
              changeLabel={user?.role === "hr" || user?.role === "accounts" ? "in your category" : (managerCategoryFilter !== "all" ? "category filter" : "showing all")}
              loading={user?.role === "hr" || user?.role === "accounts" ? !myLeadsData : !leadsData}
              testId="metric-total-leads"
            />
            {(user?.role === "manager" || user?.role === "admin") && (
              <MetricsCard
                title="Active HR Users"
                value={hrUsersData?.filter((u: any) => u.isActive).length || 0}
                icon={Clock}
                change="HR team members"
                changeLabel="currently active"
                changeType="positive"
                loading={!hrUsersData}
                testId="metric-active-hr"
              />
            )}
            {(user?.role === "manager" || user?.role === "admin") && (
              <MetricsCard
                title="Completed"
                value={leadsData?.leads?.filter((l: any) => l.status === 'completed').length || 0}
                icon={CheckCircle}
                change={managerCategoryFilter !== "all" ? managerCategoryFilter : "All Categories"}
                changeLabel={managerCategoryFilter !== "all" ? "category filter" : "showing all"}
                loading={!leadsData}
                testId="metric-completed"
              />
            )}
            {(user?.role === "manager" || user?.role === "admin") && (
              <MetricsCard
                title="Revenue Pipeline"
                value={formatRevenueDisplay(collectedAmount)}
                icon={DollarSign}
                change={managerCategoryFilter !== "all" ? managerCategoryFilter : "All Categories"}
                changeLabel={managerCategoryFilter !== "all" ? "category filter" : "total collected"}
                loading={!leadsData}
                testId="metric-revenue"
              />
            )}
          </div>

          {/* Modern Dashboard Layout */}
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 mb-8">
            {/* Left Column - Spans 2 rows */}
            <div className="lg:col-span-2 space-y-6">
              {/* Pie Chart - Wider */}
              <Card className="shadow-green-lg hover:shadow-green-bright transition-shadow duration-300 border border-border overflow-hidden">
                <CardHeader className="bg-gradient-to-r from-blue-50 to-purple-50 dark:from-blue-950 dark:to-purple-950 pb-4">
                  <CardTitle className="text-xl font-bold">
                    My Lead Status
                  </CardTitle>
                </CardHeader>
                <CardContent className="pt-6 px-6 pb-6 min-h-[390px] flex items-center justify-center">
                  <StatusChart data={roleSpecificMetrics} />
                </CardContent>
              </Card>

              {/* Allocation Strategy - Full Width Bottom */}
              <Card className="shadow-green-lg hover:shadow-green-bright transition-shadow duration-300 border border-border overflow-hidden">
                <CardHeader className="bg-gradient-to-r from-blue-50 to-purple-50 dark:from-blue-950 dark:to-purple-950 pb-4">
                  <CardTitle className="text-xl font-bold">
                    Allocation Strategy
                  </CardTitle>
                </CardHeader>
                <CardContent className="space-y-3 pt-4 pb-3 px-4 dashboard-allocation-card min-h-[550px]">
                  <div className="flex items-center justify-between p-3 bg-muted rounded-lg shadow-sm">
                    <div>
                      <p className="text-sm font-medium">Current Strategy</p>
                      <p className="text-sm text-muted-foreground">
                        Registration Based
                      </p>
                    </div>
                    <span className="status-badge status-ready-for-class text-sm px-3 py-1.5">
                      Active
                    </span>
                  </div>

                  <div className="space-y-3">
                    <h4 className="text-sm font-semibold">
                      HR Status Assignment
                    </h4>
                    <div className="space-y-2 max-h-[600px] overflow-y-auto pr-2">
                      {hrStatusAssignments.length > 0 ? (
                        hrStatusAssignments.map((hr, index) => (
                          <div
                            key={index}
                            className="bg-muted/50 rounded-lg p-3 shadow-sm"
                          >
                            <p className="font-medium text-sm mb-2 truncate">
                              {hr.name}
                            </p>
                            <div className="flex flex-wrap gap-2">
                              {Object.entries(hr.statuses).map(
                                ([status, count]) => (
                                  <span
                                    key={status}
                                    className="inline-block bg-primary/20 text-primary px-2.5 py-1 rounded-md text-xs font-medium whitespace-nowrap"
                                    data-testid={`badge-hr-status-${status}`}
                                  >
                                    {status.replace(/_/g, " ")}: {count}
                                  </span>
                                ),
                              )}
                            </div>
                          </div>
                        ))
                      ) : (
                        <p className="text-sm text-muted-foreground text-center py-4">
                          No data available
                        </p>
                      )}
                    </div>
                  </div>
                </CardContent>
              </Card>
            </div>

            {/* Right Column - 2 Cards Stacked */}
            <div className="space-y-6">
              {/* Notifications */}
              {user?.role === "manager" ? (
                <Card className="shadow-green-lg hover:shadow-green-bright transition-shadow duration-300 border border-border overflow-hidden">
                  <CardHeader className="bg-gradient-to-r from-blue-50 to-purple-50 dark:from-blue-950 dark:to-purple-950 pb-4">
                    <CardTitle className="flex items-center text-xl font-bold">
                      <Bell className="mr-2 h-5 w-5" />
                      Send Team Notification
                    </CardTitle>
                  </CardHeader>
                  <CardContent className="space-y-4 pt-4 dashboard-notification-send max-h-[500px] overflow-y-auto">
                    <div>
                      <label className="text-sm font-medium mb-2 block">
                        Title
                      </label>
                      <Input
                        placeholder="Notification title..."
                        value={notificationTitle}
                        onChange={(e) => setNotificationTitle(e.target.value)}
                        data-testid="input-notification-title"
                      />
                    </div>
                    <div>
                      <label className="text-sm font-medium mb-2 block">
                        Message
                      </label>
                      <Textarea
                        placeholder="Type your message here..."
                        rows={4}
                        value={notificationMessage}
                        onChange={(e) => setNotificationMessage(e.target.value)}
                        data-testid="textarea-notification-message"
                      />
                    </div>
                    <div>
                      <label className="text-sm font-medium mb-2 block">
                        Photo (PNG, JPG)
                      </label>
                      <div className="flex items-center gap-2">
                        <input
                          type="file"
                          accept="image/png,image/jpeg"
                          onChange={(e) => {
                            const file = e.target.files?.[0];
                            if (file) {
                              setNotificationImage(file);
                              const reader = new FileReader();
                              reader.onload = (event) => {
                                setImagePreview(event.target?.result as string);
                              };
                              reader.readAsDataURL(file);
                            }
                          }}
                          className="flex-1"
                          data-testid="input-notification-image"
                        />
                        {imagePreview && (
                          <button
                            onClick={() => {
                              setNotificationImage(null);
                              setImagePreview("");
                            }}
                            className="px-3 py-1.5 text-sm bg-red-500 text-white rounded-md hover:bg-red-600 transition-colors shadow-sm"
                            data-testid="button-remove-image"
                          >
                            Remove
                          </button>
                        )}
                      </div>
                      {imagePreview && (
                        <img
                          src={imagePreview}
                          alt="Preview"
                          className="mt-3 max-w-full h-32 object-cover rounded-lg shadow-md"
                          data-testid="img-notification-preview"
                        />
                      )}
                    </div>
                    <div>
                      <label className="text-sm font-medium mb-3 block">
                        Send To
                      </label>
                      <div className="space-y-2">
                        {[
                          { value: "hr", label: "HR Team" },
                          { value: "accounts", label: "Accounts Team" },
                          { value: "admin", label: "Admin" },
                        ].map((role) => (
                          <div
                            key={role.value}
                            className="flex items-center space-x-2"
                          >
                            <Checkbox
                              id={`role-${role.value}`}
                              checked={selectedRoles.includes(role.value)}
                              onCheckedChange={(checked) => {
                                if (checked) {
                                  setSelectedRoles([
                                    ...selectedRoles,
                                    role.value,
                                  ]);
                                } else {
                                  setSelectedRoles(
                                    selectedRoles.filter(
                                      (r) => r !== role.value,
                                    ),
                                  );
                                }
                              }}
                              data-testid={`checkbox-role-${role.value}`}
                            />
                            <label
                              htmlFor={`role-${role.value}`}
                              className="text-sm cursor-pointer"
                            >
                              {role.label}
                            </label>
                          </div>
                        ))}
                      </div>
                    </div>
                  </CardContent>
                  <div className="border-t border-border p-4">
                    <Button
                      onClick={() =>
                        sendNotificationMutation.mutate({
                          title: notificationTitle,
                          message: notificationMessage,
                          roles: selectedRoles,
                          image: notificationImage || undefined,
                        })
                      }
                      disabled={
                        !notificationTitle.trim() ||
                        !notificationMessage.trim() ||
                        selectedRoles.length === 0 ||
                        sendNotificationMutation.isPending
                      }
                      className="w-full"
                      size="default"
                      data-testid="button-send-notification"
                    >
                      <Send className="w-4 h-4 mr-2" />
                      {sendNotificationMutation.isPending
                        ? "Sending..."
                        : "Send"}
                    </Button>
                  </div>
                </Card>
              ) : (
                <Card className="shadow-green-lg hover:shadow-green-bright transition-shadow duration-300 border border-border overflow-hidden">
                  <CardHeader className="bg-gradient-to-r from-blue-50 to-purple-50 dark:from-blue-950 dark:to-purple-950 pb-4">
                    <CardTitle className="flex items-center text-xl font-bold">
                      <Bell className="mr-2 h-5 w-5" />
                      Your Notifications
                    </CardTitle>
                  </CardHeader>
                  <CardContent className="pt-4 px-4 pb-4 dashboard-notification-receive min-h-[320px]">
                    {notificationsData.length === 0 ? (
                      <div className="flex items-center justify-center h-full text-center text-muted-foreground">
                        <div>
                          <Bell className="h-12 w-12 mx-auto mb-3 opacity-30" />
                          <p className="text-sm">No notifications</p>
                        </div>
                      </div>
                    ) : (
                      <div className="space-y-3">
                        {notificationsData
                          .slice()
                          .reverse()
                          .slice(0, 1)
                          .map((notification: any) => (
                            <div
                              key={notification.id}
                              className={`border rounded-lg overflow-hidden shadow-md ${
                                notification.isRead
                                  ? "bg-muted border-border"
                                  : "bg-blue-50 dark:bg-blue-950 border-blue-200 dark:border-blue-800"
                              }`}
                              data-testid={`notification-message-${notification.id}`}
                            >
                              <div className="p-4 border-b border-border">
                                <div className="font-semibold text-sm">
                                  {notification.title}
                                </div>
                                <div className="text-sm text-muted-foreground mt-2 line-clamp-3">
                                  {notification.message}
                                </div>
                                <div className="text-xs text-muted-foreground mt-2">
                                  {new Date(
                                    notification.createdAt,
                                  ).toLocaleTimeString([], {
                                    hour: "2-digit",
                                    minute: "2-digit",
                                  })}
                                </div>
                              </div>
                              {notification.imageUrl && (
                                <div className="p-4 bg-muted/30">
                                  <img
                                    src={notification.imageUrl}
                                    alt="Notification"
                                    className="w-full h-96 object-contain rounded-md"
                                    data-testid={`notification-img-${notification.id}`}
                                  />
                                </div>
                              )}
                            </div>
                          ))}
                      </div>
                    )}
                  </CardContent>
                </Card>
              )}

              {/* Recent Activity */}
              <Card className="shadow-green-lg hover:shadow-green-bright transition-shadow duration-300 border border-border overflow-hidden">
                <CardHeader className="bg-gradient-to-r from-blue-50 to-purple-50 dark:from-blue-950 dark:to-purple-950 pb-4">
                  <CardTitle className="text-xl font-bold">
                    Recent Activity
                  </CardTitle>
                </CardHeader>
                <CardContent className="pt-4 px-4 pb-4 min-h-[320px] max-h-[450px] overflow-y-auto">
                  {!recentActivity?.length ? (
                    <div className="flex items-center justify-center h-full text-center text-muted-foreground">
                      <div>
                        <p className="text-sm">No recent activity</p>
                      </div>
                    </div>
                  ) : (
                    <div className="space-y-3">
                      {recentActivity
                        .slice(0, 12)
                        .map((activity: any, index: number) => (
                          <div
                            key={index}
                            className="p-3 bg-muted/50 rounded-lg border border-border shadow-sm hover:shadow-md transition-shadow"
                          >
                            <p className="font-medium text-sm text-foreground truncate">
                              {activity.leadName}
                            </p>
                            <p className="text-muted-foreground text-xs mt-1.5 line-clamp-2">
                              {activity.action}
                            </p>
                            <p className="text-muted-foreground text-xs mt-1.5">
                              {new Date(
                                activity.timestamp,
                              ).toLocaleTimeString()}
                            </p>
                          </div>
                        ))}
                    </div>
                  )}
                </CardContent>
              </Card>
            </div>
          </div>
        </main>
      </div>

      {/* Modals */}
      {showBulkUpload && (
        <BulkUploadModal
          isOpen={showBulkUpload}
          onClose={() => setShowBulkUpload(false)}
        />
      )}
    </div>
  );
}

