import { useState, useEffect } from "react";
import Sidebar from "@/components/Sidebar";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { Search, Calendar, User, Phone, Mail, MapPin, GraduationCap, Clock, ChevronDown, ChevronUp, History, ArrowRight, Edit, DollarSign, Plus, Trash2 } from "lucide-react";
import FloatingChatbot from "@/components/FloatingChatbot";
import { Collapsible, CollapsibleContent, CollapsibleTrigger } from "@/components/ui/collapsible";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

interface Lead {
  id: number;
  name: string;
  email: string;
  phone: string;
  location: string;
  degree: string;
  domain: string;
  sessionDays: string;
  timing: string;
  walkinDate: string;
  walkinTime: string;
  status: string;
  notes: string;
  registrationAmount: string;
  pendingAmount: string;
  partialAmount: string;
  transactionNumber: string;
  concession: string;
  createdAt: string;
  updatedAt: string;
  currentOwner: any;
  sourceManagerId: string;
  sourceManager: any;
  isActive: boolean;
}

interface LeadHistory {
  id: number;
  fromUserId: string;
  toUserId: string;
  previousStatus: string;
  newStatus: string;
  changeReason: string;
  changeData: any;
  changedAt: string;
  changedByUserId: string;
}

// Form schema for account team to update payments and status
const updatePaymentSchema = z.object({
  partialAmount: z.string().optional(),
  transactionNumber: z.string().optional(),
  concession: z.string().optional(),
  status: z.enum(["new", "scheduled", "completed", "not_interested", "not_available", "no_show", "reschedule", "pending", "ready_for_class", "pending_but_ready"]).optional(),
  changeReason: z.string().min(1, "Please provide a reason for this update"),
});

const statusOptions = [
  { value: 'new', label: 'New' },
  { value: 'scheduled', label: 'Scheduled' },
  { value: 'completed', label: 'Completed' },
  { value: 'not_interested', label: 'Not Interested' },
  { value: 'not_available', label: 'Not Available' },
  { value: 'no_show', label: 'No Show' },
  { value: 'reschedule', label: 'Reschedule' },
  { value: 'pending', label: 'Pending' },
  { value: 'ready_for_class', label: 'Ready for Class' },
  { value: 'pending_but_ready', label: 'Pending but Ready' },
];

type UpdatePaymentForm = z.infer<typeof updatePaymentSchema>;

export default function MyCompletionPage() {
  const { user, isAuthenticated, isLoading } = useAuth();
  const { toast } = useToast();
  const [searchTerm, setSearchTerm] = useState("");
  const [expandedLeadId, setExpandedLeadId] = useState<number | null>(null);
  const [editingLeadId, setEditingLeadId] = useState<number | null>(null);
  const [editDialogOpen, setEditDialogOpen] = useState(false);
  const [transactions, setTransactions] = useState<string[]>([]);
  const [newTransactionNumber, setNewTransactionNumber] = useState("");
  const [selectedCategory, setSelectedCategory] = useState(() => {
    if (typeof window !== 'undefined') {
      return localStorage.getItem("selectedCategory") || "Client Hiring";
    }
    return "Client Hiring";
  });

  useEffect(() => {
    const handleStorageChange = () => {
      const newCategory = localStorage.getItem("selectedCategory") || "Client Hiring";
      setSelectedCategory(newCategory);
    };

    window.addEventListener('storage', handleStorageChange);
    return () => window.removeEventListener('storage', handleStorageChange);
  }, []);

  const form = useForm<UpdatePaymentForm>({
    resolver: zodResolver(updatePaymentSchema),
    defaultValues: {
      partialAmount: '',
      transactionNumber: '',
      concession: '',
      status: 'pending',
      changeReason: '',
    },
  });

  // Sync transactions array with form transactionNumber field
  useEffect(() => {
    const txnString = transactions.length > 0 ? transactions.join(', ') : '';
    form.setValue('transactionNumber', txnString);
  }, [transactions, form]);

  // Update lead mutation for account team
  const updateLeadMutation = useMutation({
    mutationFn: async ({ leadId, data }: { leadId: number; data: UpdatePaymentForm }) => {
      const response = await apiRequest('PUT', `/api/leads/${leadId}`, data);
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Lead Updated",
        description: "Payment information updated successfully.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/my/completed"] });
      queryClient.invalidateQueries({ queryKey: ["/api/leads"] });
      setEditDialogOpen(false);
      setEditingLeadId(null);
      form.reset();
    },
    onError: () => {
      toast({
        title: "Update Failed",
        description: "Failed to update lead. Please try again.",
        variant: "destructive",
      });
    }
  });

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'completed':
        return 'status-completed';
      case 'pending':
        return 'status-pending';
      case 'ready_for_class':
        return 'status-ready-for-class';
      default:
        return 'status-completed';
    }
  };

  // Fetch my completed leads with category filter
  const { data, isLoading: dataLoading, error } = useQuery({
    queryKey: ["/api/my/completed", searchTerm, selectedCategory],
    queryFn: async () => {
      const params = new URLSearchParams();
      if (searchTerm) params.append('search', searchTerm);
      params.append('category', selectedCategory);
      const url = `/api/my/completed?${params}`;
      const response = await fetch(url, { credentials: 'include' });
      if (!response.ok) throw new Error("Failed to fetch completed leads");
      return response.json();
    },
    retry: false,
  });

  // Fetch lead history for expanded lead
  const { data: leadHistory } = useQuery({
    queryKey: ["/api/leads", expandedLeadId, "history"],
    retry: false,
    enabled: !!expandedLeadId,
  });

  // Filter leads based on search term
  const filteredLeads = data?.leads?.filter((lead: Lead) => 
    lead.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    lead.email?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    lead.phone?.includes(searchTerm)
  ) || [];

  if (isLoading || dataLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary"></div>
      </div>
    );
  }

  if (!isAuthenticated) {
    return null;
  }

  if (error) {
    return (
      <div className="landing-background min-h-screen flex relative">
        {/* Decorative shapes */}
        <div className="absolute top-0 left-0 w-64 h-64 bg-gradient-to-br from-[#11754c]/40 to-transparent rounded-full blur-3xl pointer-events-none"></div>
        <div className="absolute top-1/3 right-0 w-80 h-80 bg-gradient-to-br from-[#04e284]/35 to-transparent rounded-full blur-3xl pointer-events-none"></div>
        <div className="absolute bottom-0 left-1/2 transform -translate-x-1/2 w-full h-40 bg-gradient-to-t from-[#11754c]/30 to-transparent skew-y-3 pointer-events-none"></div>
        <Sidebar />
        <div className="flex-1 flex flex-col overflow-hidden relative z-10">
          <div className="flex items-center justify-center h-64">
            <Card>
              <CardContent className="p-8 text-center">
                <p className="text-destructive">Failed to load completed leads. Please try again.</p>
              </CardContent>
            </Card>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="landing-background min-h-screen flex relative">
      {/* Decorative shapes */}
      <div className="absolute top-0 left-0 w-64 h-64 bg-gradient-to-br from-[#11754c]/40 to-transparent rounded-full blur-3xl pointer-events-none"></div>
      <div className="absolute top-1/3 right-0 w-80 h-80 bg-gradient-to-br from-[#04e284]/35 to-transparent rounded-full blur-3xl pointer-events-none"></div>
      <div className="absolute bottom-0 left-1/2 transform -translate-x-1/2 w-full h-40 bg-gradient-to-t from-[#11754c]/30 to-transparent skew-y-3 pointer-events-none"></div>

      <FloatingChatbot />
      <Sidebar />

      {/* Main Content */}
      <div className="flex-1 flex flex-col overflow-hidden relative z-10">
        {/* Header */}
        <header className="bg-card border-b border-border px-6 py-4">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-2xl font-bold text-foreground" data-testid="heading-my-completion">
                My Completion
              </h1>
              <p className="text-sm text-muted-foreground">
                View leads you've completed and their current status in accounts processing
              </p>
            </div>
          </div>
        </header>

        {/* Search */}
        <div className="bg-card border-b border-border px-6 py-4">
          <div className="relative">
            <Input
              placeholder="Search by name, email, or phone..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full max-w-md pl-10"
              data-testid="input-search-completed"
            />
            <Search className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
          </div>
        </div>

        {/* Content */}
        <main className="flex-1 overflow-y-auto p-6">
          {/* Completed Leads List */}
          <div className="space-y-4">
          {filteredLeads.length === 0 ? (
            <Card>
              <CardContent className="p-8 text-center">
                <div className="flex flex-col items-center">
                  <History className="h-12 w-12 text-muted-foreground mb-4" />
                  <h3 className="text-lg font-semibold text-foreground mb-2">
                    No Completed Leads
                  </h3>
                  <p className="text-muted-foreground">
                    {searchTerm 
                      ? "No completed leads match your search criteria." 
                      : "You haven't completed any leads yet. Completed leads will appear here."
                    }
                  </p>
                </div>
              </CardContent>
            </Card>
          ) : (
            filteredLeads.map((lead: Lead) => (
              <Card key={lead.id} className="overflow-hidden">
                <CardHeader className="pb-3">
                  <div className="flex items-start justify-between">
                    <div className="flex-1">
                      <CardTitle className="text-lg font-semibold text-foreground">
                        {lead.name}
                        <Badge className={`ml-3 ${getStatusColor(lead.status)}`}>
                          {lead.status.replace('_', ' ')}
                        </Badge>
                      </CardTitle>
                      <div className="flex flex-wrap gap-4 mt-2 text-sm text-muted-foreground">
                        <div className="flex items-center">
                          <Mail className="h-4 w-4 mr-1" />
                          {lead.email}
                        </div>
                        {lead.phone && (
                          <div className="flex items-center">
                            <Phone className="h-4 w-4 mr-1" />
                            {lead.phone}
                          </div>
                        )}
                        {lead.location && (
                          <div className="flex items-center">
                            <MapPin className="h-4 w-4 mr-1" />
                            {lead.location}
                          </div>
                        )}
                      </div>
                    </div>
                    <Collapsible>
                      <CollapsibleTrigger asChild>
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => setExpandedLeadId(expandedLeadId === lead.id ? null : lead.id)}
                          data-testid={`button-expand-lead-${lead.id}`}
                        >
                          {expandedLeadId === lead.id ? (
                            <ChevronUp className="h-4 w-4" />
                          ) : (
                            <ChevronDown className="h-4 w-4" />
                          )}
                          Details
                        </Button>
                      </CollapsibleTrigger>
                      <CollapsibleContent>
                        <CardContent className="pt-4 border-t mt-4">
                          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                            {/* Lead Details */}
                            <div className="space-y-4">
                              <h4 className="font-semibold text-foreground">Complete Lead Information</h4>

                              <div className="flex items-center text-sm">
                                <span className="font-medium mr-2">Lead ID:</span>
                                <span className="font-mono text-xs bg-muted px-2 py-1 rounded">#{lead.id}</span>
                              </div>

                              {lead.degree && (
                                <div className="flex items-center text-sm">
                                  <GraduationCap className="h-4 w-4 mr-2 text-muted-foreground" />
                                  <span className="font-medium mr-2">Degree:</span>
                                  <span>{lead.degree}</span>
                                </div>
                              )}

                              {lead.domain && (
                                <div className="flex items-center text-sm">
                                  <span className="font-medium mr-2">Domain:</span>
                                  <span>{lead.domain}</span>
                                </div>
                              )}

                              {lead.sessionDays && (
                                <div className="flex items-center text-sm">
                                  <span className="font-medium mr-2">Session Days:</span>
                                  <span>{lead.sessionDays}</span>
                                </div>
                              )}

                              {lead.timing && (
                                <div className="flex items-center text-sm">
                                  <Clock className="h-4 w-4 mr-2 text-muted-foreground" />
                                  <span className="font-medium mr-2">Timing:</span>
                                  <span>{lead.timing}</span>
                                </div>
                              )}

                              {(lead.walkinDate || lead.walkinTime) && (
                                <div className="flex items-center text-sm">
                                  <Calendar className="h-4 w-4 mr-2 text-muted-foreground" />
                                  <span className="font-medium mr-2">Walk-in:</span>
                                  <span>
                                    {lead.walkinDate && lead.walkinTime
                                      ? `${lead.walkinDate} at ${lead.walkinTime}`
                                      : lead.walkinDate || lead.walkinTime
                                    }
                                  </span>
                                </div>
                              )}

                              <div className="flex items-center text-sm">
                                <span className="font-medium mr-2">Status:</span>
                                <Badge className={`text-xs ${getStatusColor(lead.status)}`}>
                                  {lead.isActive ? 'Active' : 'Inactive'} - {lead.status.replace('_', ' ')}
                                </Badge>
                              </div>

                              <div className="flex items-center text-sm">
                                <span className="font-medium mr-2">Created:</span>
                                <span className="text-muted-foreground">
                                  {new Date(lead.createdAt).toLocaleDateString()} at{' '}
                                  {new Date(lead.createdAt).toLocaleTimeString()}
                                </span>
                              </div>

                              {lead.notes && (
                                <div className="text-sm">
                                  <span className="font-medium">Notes:</span>
                                  <p className="mt-1 text-muted-foreground">{lead.notes}</p>
                                </div>
                              )}
                            </div>

                            {/* Current Handler & Management */}
                            <div className="space-y-4">
                              <h4 className="font-semibold text-foreground">Handler & Management Details</h4>

                              {lead.sourceManager ? (
                                <div className="flex items-center text-sm">
                                  <User className="h-4 w-4 mr-2 text-muted-foreground" />
                                  <span className="font-medium mr-2">Created by:</span>
                                  <span>
                                    {lead.sourceManager.firstName} {lead.sourceManager.lastName}
                                    <Badge variant="outline" className="ml-2 text-xs">
                                      {lead.sourceManager.role}
                                    </Badge>
                                  </span>
                                </div>
                              ) : (
                                <div className="flex items-center text-sm">
                                  <User className="h-4 w-4 mr-2 text-muted-foreground" />
                                  <span className="font-medium mr-2">Created by:</span>
                                  <span className="text-muted-foreground">System/Unknown</span>
                                </div>
                              )}

                              {lead.currentOwner ? (
                                <div className="flex items-center text-sm">
                                  <User className="h-4 w-4 mr-2 text-muted-foreground" />
                                  <span className="font-medium mr-2">Currently assigned to:</span>
                                  <span>
                                    {lead.currentOwner.firstName} {lead.currentOwner.lastName}
                                    <Badge variant="outline" className="ml-2 text-xs">
                                      {lead.currentOwner.role}
                                    </Badge>
                                  </span>
                                </div>
                              ) : (
                                <div className="flex items-center text-sm">
                                  <User className="h-4 w-4 mr-2 text-muted-foreground" />
                                  <span className="font-medium mr-2">Currently assigned to:</span>
                                  <span className="text-muted-foreground">No handler assigned</span>
                                </div>
                              )}

                              <div className="text-sm">
                                <span className="font-medium">Last Updated:</span>
                                <p className="text-muted-foreground">
                                  {new Date(lead.updatedAt).toLocaleDateString()} at{' '}
                                  {new Date(lead.updatedAt).toLocaleTimeString()}
                                </p>
                              </div>

                              {/* Financial Information */}
                              <div className="mt-6 p-4 bg-primary/5 rounded-lg border border-primary/20">
                                <h5 className="font-semibold text-foreground mb-3 flex items-center">
                                  <DollarSign className="h-4 w-4 mr-2" />
                                  Payment Information
                                </h5>
                                <div className="space-y-2 text-sm">
                                  {(() => {
                                    const TOTAL_AMOUNT = 7000;
                                    const regAmount = lead.registrationAmount ? parseFloat(lead.registrationAmount) : 0;
                                    const concessionAmount = lead.concession ? parseFloat(lead.concession) : 0;
                                    const partAmount = lead.partialAmount ? parseFloat(lead.partialAmount) : 0;
                                    const pendingAmount = TOTAL_AMOUNT - (regAmount + concessionAmount + partAmount);
                                    const collectedAmount = regAmount + partAmount;

                                    return (
                                      <>
                                        <div className="flex justify-between">
                                          <span className="text-muted-foreground">Registration Amount:</span>
                                          <span className="font-medium">
                                            {regAmount > 0 ? `${regAmount.toFixed(2)}` : ''}
                                          </span>
                                        </div>
                                        <div className="flex justify-between">
                                          <span className="text-muted-foreground">Partial Amount:</span>
                                          <span className="font-medium text-orange-600 dark:text-orange-400">
                                            {partAmount > 0 ? `${partAmount.toFixed(2)}` : ''}
                                          </span>
                                        </div>
                                        <div className="flex justify-between">
                                          <span className="text-muted-foreground">Concession:</span>
                                          <span className="font-medium text-destructive">
                                            {concessionAmount > 0 ? `${concessionAmount.toFixed(2)}` : ''}
                                          </span>
                                        </div>
                                        <div className="flex justify-between pt-2 border-t border-border">
                                          <span className="text-muted-foreground font-semibold">Collected Amount:</span>
                                          <span className="font-bold text-green-600 dark:text-green-400">
                                            {collectedAmount.toFixed(2)}
                                          </span>
                                        </div>
                                        <div className="flex justify-between">
                                          <span className="text-muted-foreground font-semibold">Pending Amount:</span>
                                          <span className="font-bold text-blue-600 dark:text-blue-400">
                                            {pendingAmount.toFixed(2)}
                                          </span>
                                        </div>
                                      </>
                                    );
                                  })()}

                                  <div className="space-y-2">
                                    <span className="text-muted-foreground block">Transaction Numbers:</span>
                                    {lead.transactionNumber ? (
                                      <div className="flex flex-wrap gap-2">
                                        {(lead.transactionNumber.includes(',') 
                                          ? lead.transactionNumber.split(',').map(t => t.trim())
                                          : [lead.transactionNumber]
                                        ).map((txn, idx) => (
                                          <span key={idx} className="px-2 py-1 bg-blue-100 dark:bg-blue-900 text-blue-800 dark:text-blue-200 rounded text-xs font-medium">
                                            {txn}
                                          </span>
                                        ))}
                                      </div>
                                    ) : (
                                      <span className="text-muted-foreground">Not set</span>
                                    )}
                                  </div>
                                </div>
                                <Button
                                  size="sm"
                                  className="w-full mt-4"
                                  onClick={() => {
                                    setEditingLeadId(lead.id);
                                    const txnList = lead.transactionNumber 
                                      ? lead.transactionNumber.split(',').map(t => t.trim())
                                      : [];
                                    setTransactions(txnList);
                                    setNewTransactionNumber('');
                                    form.reset({
                                      partialAmount: lead.partialAmount || '',
                                      transactionNumber: lead.transactionNumber || '',
                                      concession: lead.concession || '',
                                      status: lead.status as "pending" | "ready_for_class",
                                      changeReason: '',
                                    });
                                    setEditDialogOpen(true);
                                  }}
                                  data-testid={`button-edit-payment-${lead.id}`}
                                >
                                  <Edit className="h-4 w-4 mr-2" />
                                  Update Payment Info
                                </Button>
                              </div>
                            </div>
                          </div>

                          {/* Status Information */}
                          <div className="mt-6 p-4 bg-muted/50 rounded-lg">
                            <div className="flex items-center justify-between">
                              <div>
                                <h5 className="font-medium text-foreground">Current Status</h5>
                                <p className="text-sm text-muted-foreground mt-1">
                                  This lead has been forwarded to the accounts team and is being processed.
                                </p>
                              </div>
                              <Badge className={getStatusColor(lead.status)}>
                                {lead.status.replace('_', ' ')}
                              </Badge>
                            </div>
                          </div>

                          {/* Update History */}
                          <div className="mt-6 space-y-4">
                            <h4 className="font-semibold text-foreground flex items-center">
                              <History className="h-4 w-4 mr-2" />
                              Update History
                            </h4>
                            {leadHistory && leadHistory.length > 0 ? (
                              <div className="space-y-3">
                                {leadHistory.map((historyItem: any, index: number) => (
                                  <div key={historyItem.id} className="p-3 border border-border rounded-lg bg-card/50">
                                    <div className="flex items-start justify-between mb-2">
                                      <div className="flex items-center">
                                        <User className="h-4 w-4 mr-2 text-muted-foreground" />
                                        <span className="font-medium text-sm">
                                          {historyItem.changedByUser?.firstName} {historyItem.changedByUser?.lastName}
                                        </span>
                                        <Badge variant="outline" className="ml-2 text-xs">
                                          {historyItem.changedByUser?.role}
                                        </Badge>
                                      </div>
                                      <span className="text-xs text-muted-foreground">
                                        {new Date(historyItem.changedAt).toLocaleDateString()} at{' '}
                                        {new Date(historyItem.changedAt).toLocaleTimeString()}
                                      </span>
                                    </div>

                                    <div className="text-sm">
                                      <div className="flex items-center mb-1">
                                        <ArrowRight className="h-3 w-3 mr-2 text-muted-foreground" />
                                        <span>Status changed from </span>
                                        <Badge variant="outline" className="mx-1 text-xs">
                                          {historyItem.previousStatus}
                                        </Badge>
                                        <span> to </span>
                                        <Badge variant="outline" className="mx-1 text-xs">
                                          {historyItem.newStatus}
                                        </Badge>
                                      </div>

                                      {historyItem.changeReason && (
                                        <p className="text-muted-foreground mt-1">
                                          <strong>Reason:</strong> {historyItem.changeReason}
                                        </p>
                                      )}

                                      {historyItem.changeData && (() => {
                                        try {
                                          const changeData = JSON.parse(historyItem.changeData);
                                          const changedFields = changeData.changedFields || [];
                                          const txnChange = changedFields.find((f: any) => f.field === 'transactionNumber');

                                          return (
                                            <div className="mt-2 space-y-2">
                                              {txnChange && (
                                                <div className="p-2 bg-blue-50 dark:bg-blue-950 rounded border border-blue-200 dark:border-blue-800">
                                                  <p className="font-medium text-xs text-blue-900 dark:text-blue-100 mb-1">
                                                    Transaction Number Updated
                                                  </p>
                                                  <div className="text-xs space-y-1">
                                                    <div>
                                                      <span className="text-muted-foreground">Previous: </span>
                                                      <span className="font-mono text-blue-700 dark:text-blue-300">
                                                        {txnChange.oldValue || ''}
                                                      </span>
                                                    </div>
                                                    <div>
                                                      <span className="text-muted-foreground">Updated: </span>
                                                      <span className="font-mono text-blue-700 dark:text-blue-300">
                                                        {txnChange.newValue || ''}
                                                      </span>
                                                    </div>
                                                  </div>
                                                </div>
                                              )}

                                              {changedFields.length > 0 && (
                                                <details className="mt-1">
                                                  <summary className="cursor-pointer text-muted-foreground hover:text-foreground text-xs">
                                                    View all changes ({changedFields.length})
                                                  </summary>
                                                  <pre className="text-xs mt-1 p-2 bg-muted rounded overflow-x-auto">
                                                    {JSON.stringify(changeData, null, 2)}
                                                  </pre>
                                                </details>
                                              )}
                                            </div>
                                          );
                                        } catch (e) {
                                          return (
                                            <details className="mt-2">
                                              <summary className="cursor-pointer text-muted-foreground hover:text-foreground">
                                                View detailed changes
                                              </summary>
                                              <pre className="text-xs mt-1 p-2 bg-muted rounded overflow-x-auto">
                                                {historyItem.changeData}
                                              </pre>
                                            </details>
                                          );
                                        }
                                      })()}
                                    </div>
                                  </div>
                                ))}
                              </div>
                            ) : (
                              <p className="text-sm text-muted-foreground">
                                No history available for this lead.
                              </p>
                            )}
                          </div>
                        </CardContent>
                      </CollapsibleContent>
                    </Collapsible>
                  </div>
                </CardHeader>
              </Card>
            ))
          )}
          </div>

          {/* Summary */}
          {filteredLeads.length > 0 && (
            <Card className="mt-6">
              <CardContent className="p-6">
                <div className="text-center text-muted-foreground">
                  <p>
                    Showing {filteredLeads.length} of {data?.total || 0} completed leads
                  </p>
                </div>
              </CardContent>
            </Card>
          )}
        </main>
      </div>

      {/* Edit Payment Dialog */}
      <Dialog open={editDialogOpen} onOpenChange={setEditDialogOpen}>
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle>Update Payment Information</DialogTitle>
            <DialogDescription>
              Update partial payment amount and status for this lead.
            </DialogDescription>
          </DialogHeader>

          <Form {...form}>
            <form onSubmit={form.handleSubmit((data) => {
              if (editingLeadId) {
                updateLeadMutation.mutate({ leadId: editingLeadId, data });
              }
            })} className="space-y-6">

              {/* Partial Amount */}
              <FormField
                control={form.control}
                name="partialAmount"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Partial Amount ()</FormLabel>
                    <FormControl>
                      <Input
                        {...field}
                        type="number"
                        step="0.01"
                        placeholder="Enter partial amount received"
                        data-testid="input-partial-amount"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              {/* Multiple Transaction Numbers */}
              <div className="space-y-3">
                <FormLabel>Transaction Numbers</FormLabel>
                <div className="flex gap-2">
                  <Input
                    type="text"
                    placeholder="Enter transaction or reference number"
                    value={newTransactionNumber}
                    onChange={(e) => setNewTransactionNumber(e.target.value)}
                    onKeyPress={(e) => {
                      if (e.key === 'Enter') {
                        e.preventDefault();
                        if (newTransactionNumber.trim()) {
                          setTransactions([...transactions, newTransactionNumber.trim()]);
                          setNewTransactionNumber('');
                        }
                      }
                    }}
                    data-testid="input-transaction-number"
                  />
                  <Button
                    type="button"
                    variant="outline"
                    onClick={() => {
                      if (newTransactionNumber.trim()) {
                        setTransactions([...transactions, newTransactionNumber.trim()]);
                        setNewTransactionNumber('');
                      }
                    }}
                    data-testid="button-add-transaction"
                  >
                    <Plus className="h-4 w-4" />
                  </Button>
                </div>
                {transactions.length > 0 && (
                  <div className="flex flex-wrap gap-2 pt-2">
                    {transactions.map((txn, idx) => (
                      <div key={idx} className="px-3 py-1 bg-blue-100 dark:bg-blue-900 text-blue-800 dark:text-blue-200 rounded-full text-sm flex items-center gap-2">
                        <span className="font-medium">{txn}</span>
                        <button
                          type="button"
                          onClick={() => setTransactions(transactions.filter((_, i) => i !== idx))}
                          className="hover:opacity-70"
                          data-testid={`button-remove-transaction-${idx}`}
                        >
                          <Trash2 className="h-3 w-3" />
                        </button>
                      </div>
                    ))}
                  </div>
                )}
                <input
                  type="hidden"
                  {...form.register('transactionNumber')}
                  value={transactions.length > 0 ? transactions.join(', ') : ''}
                />
              </div>

              {/* Concession */}
              <FormField
                control={form.control}
                name="concession"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Concession Amount ()</FormLabel>
                    <FormControl>
                      <Input
                        {...field}
                        type="number"
                        step="0.01"
                        placeholder="Enter concession amount if any"
                        data-testid="input-concession"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              {/* Status */}
              <FormField
                control={form.control}
                name="status"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Status</FormLabel>
                    <Select onValueChange={field.onChange} defaultValue={field.value}>
                      <FormControl>
                        <SelectTrigger data-testid="select-status">
                          <SelectValue placeholder="Select status" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        {statusOptions.map((option) => (
                          <SelectItem key={option.value} value={option.value}>
                            {option.label}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              {/* Change Reason */}
              <FormField
                control={form.control}
                name="changeReason"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Reason for Update</FormLabel>
                    <FormControl>
                      <Input
                        {...field}
                        placeholder="Enter reason for this update"
                        data-testid="input-change-reason"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <DialogFooter>
                <Button
                  type="button"
                  variant="outline"
                  onClick={() => {
                    setEditDialogOpen(false);
                    form.reset();
                  }}
                  data-testid="button-cancel"
                >
                  Cancel
                </Button>
                <Button
                  type="submit"
                  disabled={updateLeadMutation.isPending}
                  data-testid="button-save"
                >
                  {updateLeadMutation.isPending ? 'Saving...' : 'Save Changes'}
                </Button>
              </DialogFooter>
            </form>
          </Form>
        </DialogContent>
      </Dialog>
    </div>
  );
}

