import { useState, useRef, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Eye, EyeOff, Loader2 } from "lucide-react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import logoImage from "@assets/Gemini_Generated_Image_kis2mfkis2mfkis2 (2)_1764051974976.png";
import FloatingChatbot from "@/components/FloatingChatbot";

const CATEGORIES = [
  "Client Hiring",
  "Technical Hiring",
  "Talent Acquisition Executive",
  "Medical Coding"
];

const loginSchema = z.object({
  email: z.string().email("Please enter a valid email address"),
  password: z.string().min(1, "Password is required"),
});

type LoginForm = z.infer<typeof loginSchema>;

export default function Login() {
  const [showPassword, setShowPassword] = useState(false);
  const [rememberMe, setRememberMe] = useState(false);
  const [generalError, setGeneralError] = useState("");
  const [loginSuccess, setLoginSuccess] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState("Medical Coding");
  const [userRole, setUserRole] = useState<string | null>(null);
  const [userName, setUserName] = useState<string | null>(null);
  const { toast } = useToast();

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm<LoginForm>({
    resolver: zodResolver(loginSchema),
  });

  useEffect(() => {
    // Auto-focus email input after component mounts
    const emailInput = document.querySelector('input[type="email"]') as HTMLInputElement;
    if (emailInput) {
      setTimeout(() => emailInput.focus(), 100);
    }
  }, []);

  const loginMutation = useMutation({
    mutationFn: async (data: LoginForm) => {
      const response = await apiRequest("POST", "/api/auth/login", data);
      return response.json();
    },
    onSuccess: async () => {
      // Fetch user data to check role
      try {
        const userResponse = await fetch("/api/auth/user");
        const user = await userResponse.json();
        setUserRole(user.role);
        setUserName(user.fullName || user.email);

        if (user.role === 'hr') {
          // For HR users, show category selection
          setLoginSuccess(true);
        } else {
          // For other roles, redirect directly
          window.location.href = "/";
        }
      } catch (error) {
        window.location.href = "/";
      }
    },
    onError: (error: Error) => {
      const errorMessage = error.message || "Invalid email or password";
      setGeneralError(errorMessage);
      toast({
        title: "Login Failed",
        description: errorMessage,
        variant: "destructive",
      });
    },
  });

  const handleCategorySelect = () => {
    // Store selected category in localStorage
    localStorage.setItem("selectedCategory", selectedCategory);
    // Redirect to dashboard
    window.location.href = "/";
  };

  const onSubmit = (data: LoginForm) => {
    setGeneralError("");
    loginMutation.mutate(data);
  };

  return (
    <div className="landing-background min-h-screen flex items-center justify-center p-4 relative">
      {/* Decorative HR-themed shape - top left */}
      <div className="absolute top-0 left-0 w-64 h-64 bg-gradient-to-br from-[#11754c]/40 to-transparent rounded-full blur-3xl pointer-events-none"></div>

      {/* Decorative shape - middle right */}
      <div className="absolute top-1/3 right-0 w-80 h-80 bg-gradient-to-br from-[#04e284]/35 to-transparent rounded-full blur-3xl pointer-events-none"></div>

      {/* Diagonal accent shape - bottom */}
      <div className="absolute bottom-0 left-1/2 transform -translate-x-1/2 w-full h-40 bg-gradient-to-t from-[#11754c]/30 to-transparent skew-y-3 pointer-events-none"></div>

      {/* Floating Chatbot Button */}
      <FloatingChatbot />

      {/* Center: Login Form Section */}
      <div className="w-full relative z-10 flex justify-center items-center min-h-screen">
        <div className="px-4 max-w-lg w-full">
          {/* Logo Section */}
          <div className="text-center mb-12">
            <img 
              src={logoImage}
              alt="VHomofi HRM Portal Logo"
              className="h-40 object-contain mx-auto"
            />
          </div>

          {/* Login Container */}
          <div className="bg-white/70 dark:bg-[#1a1a1a]/70 backdrop-blur-2xl rounded-3xl p-12 shadow-2xl border border-white/30 dark:border-[#333333]/30">

          {/* Category Selection - Shown after HR login */}
          {loginSuccess && userRole === 'hr' ? (
            <div className="space-y-6">
              <div className="text-center mb-8">
                <h2 className="text-2xl font-bold text-[#11754c] dark:text-[#04e284] mb-2">Welcome, {userName}</h2>
                <p className="text-[#666666] dark:text-[#999999]">Select a hiring category to view leads</p>
              </div>

              <div>
                <label className="block text-sm font-medium text-[#1a1a1a] dark:text-[#f5f5f5] mb-3">
                  Hiring Category
                </label>
                <Select value={selectedCategory} onValueChange={setSelectedCategory}>
                  <SelectTrigger className="w-full px-5 py-4 text-base border border-[#e0e0e0] dark:border-[#333333] rounded-lg bg-[#F9F9F9] dark:bg-[#2a2a2a] text-[#1a1a1a] dark:text-[#f5f5f5] focus:border-[#11754c] dark:focus:border-[#04e284]" data-testid="select-category">
                    <SelectValue placeholder="Select a category" />
                  </SelectTrigger>
                  <SelectContent>
                    {CATEGORIES.map((category) => (
                      <SelectItem key={category} value={category}>
                        {category}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              <Button
                type="button"
                onClick={handleCategorySelect}
                className="w-full py-3 px-4 rounded-md bg-[#11754c] hover:bg-[#04e284] text-white font-medium transition-all duration-200"
                data-testid="button-category-select"
              >
                Continue to Dashboard
              </Button>
            </div>
          ) : (
            <>
              {/* Error Message */}
              {generalError && (
                <div className="mb-6 p-4 rounded-md bg-[#D62828] bg-opacity-10 border border-[#D62828]">
                  <p className="text-[#D62828] text-sm font-medium">{generalError}</p>
                </div>
              )}

          <form onSubmit={handleSubmit(onSubmit)} className="space-y-5">
            {/* Username/Email Field */}
            <div>
              <label htmlFor="email" className="block text-sm font-medium text-[#1a1a1a] dark:text-[#f5f5f5] mb-2">
                Username / Email
              </label>
              <input
                id="email"
                type="email"
                placeholder="Enter your email or username"
                className="w-full px-5 py-4 text-base border border-[#e0e0e0] dark:border-[#333333] rounded-lg bg-[#F9F9F9] dark:bg-[#2a2a2a] text-[#1a1a1a] dark:text-[#f5f5f5] placeholder-[#999999] focus:outline-none focus:border-[#11754c] dark:focus:border-[#04e284] transition-colors"
                data-testid="input-email"
                {...register("email")}
              />
              {errors.email && (
                <p className="mt-2 text-sm text-[#D62828]">{errors.email.message}</p>
              )}
            </div>

            {/* Password Field */}
            <div>
              <label htmlFor="password" className="block text-sm font-medium text-[#1a1a1a] dark:text-[#f5f5f5] mb-2">
                Password
              </label>
              <div className="relative">
                <input
                  id="password"
                  type={showPassword ? "text" : "password"}
                  placeholder="Enter your password"
                  className="w-full px-5 py-4 pr-14 text-base border border-[#e0e0e0] dark:border-[#333333] rounded-lg bg-[#F9F9F9] dark:bg-[#2a2a2a] text-[#1a1a1a] dark:text-[#f5f5f5] placeholder-[#999999] focus:outline-none focus:border-[#11754c] dark:focus:border-[#04e284] transition-colors"
                  data-testid="input-password"
                  {...register("password")}
                />
                <button
                  type="button"
                  onClick={() => setShowPassword(!showPassword)}
                  className="absolute right-3 top-3 text-[#666666] dark:text-[#999999] hover:text-[#1a1a1a] dark:hover:text-[#f5f5f5]"
                  data-testid="button-toggle-password"
                >
                  {showPassword ? (
                    <EyeOff className="w-5 h-5" />
                  ) : (
                    <Eye className="w-5 h-5" />
                  )}
                </button>
              </div>
              {errors.password && (
                <p className="mt-2 text-sm text-[#D62828]">{errors.password.message}</p>
              )}
            </div>

            {/* Remember Me & Forgot Password */}
            <div className="flex items-center justify-between">
              <label className="flex items-center cursor-pointer">
                <input
                  type="checkbox"
                  checked={rememberMe}
                  onChange={(e) => setRememberMe(e.target.checked)}
                  className="w-4 h-4 rounded border-[#e0e0e0] text-[#11754c] focus:ring-[#11754c]"
                />
                <span className="ml-2 text-sm text-[#666666] dark:text-[#999999]">Remember me</span>
              </label>
              <a href="#" className="text-sm text-[#11754c] hover:text-[#04e284] font-medium transition-colors">
                Forgot Password?
              </a>
            </div>

            {/* Sign In Button */}
            <Button
              type="submit"
              disabled={loginMutation.isPending}
              className="w-full py-3 px-4 rounded-md bg-[#11754c] hover:bg-[#04e284] text-white font-medium transition-all duration-200 flex items-center justify-center space-x-2 disabled:opacity-80"
              data-testid="button-login-submit"
              style={{
                backgroundColor: loginMutation.isPending ? "#04e284" : "#11754c",
              }}
            >
              {loginMutation.isPending ? (
                <>
                  <Loader2 className="w-5 h-5 animate-spin" />
                  <span>Signing in...</span>
                </>
              ) : (
                <span>Sign In</span>
              )}
            </Button>
              </form>
            </>
          )}

          {/* Footer Text */}
          {!loginSuccess && (
            <p className="text-center text-sm text-[#666666] dark:text-[#999999] mt-8">
              Secure login to your HRM Portal
            </p>
          )}
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/components/CreateUserModal.ts.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { UserPlus, X, Eye, EyeOff } from "lucide-react";

interface CreateUserModalProps {
  isOpen: boolean;
  onClose: () => void;
  onSuccess: () => void;
}

const createUserSchema = z.object({
  fullName: z.string().min(2, "Full name must be at least 2 characters"),
  username: z.string().min(3, "Username must be at least 3 characters"),
  email: z.string().email("Please enter a valid email address"),
  role: z.enum(["hr", "accounts", "admin"], {
    required_error: "Please select a role",
  }),
  password: z.string().min(6, "Password must be at least 6 characters"),
});

type CreateUserForm = z.infer<typeof createUserSchema>;

export default function CreateUserModal({ isOpen, onClose, onSuccess }: CreateUserModalProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [showPassword, setShowPassword] = useState(false);

  const form = useForm<CreateUserForm>({
    resolver: zodResolver(createUserSchema),
    defaultValues: {
      fullName: "",
      username: "",
      email: "",
      role: undefined,
      password: "",
    },
  });

  const createUserMutation = useMutation({
    mutationFn: async (data: CreateUserForm) => {
      const response = await apiRequest("POST", "/api/users", data);
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "User Created",
        description: "New user has been created successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
      form.reset();
      onSuccess();
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/login";
        }, 500);
        return;
      }
      toast({
        title: "Creation Failed",
        description: error.message || "There was an error creating the user",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: CreateUserForm) => {
    createUserMutation.mutate(data);
  };

  const handleClose = () => {
    form.reset();
    onClose();
  };

  const generateUsername = (fullName: string) => {
    const cleanName = fullName.toLowerCase().replace(/\s+/g, '.');
    const timestamp = Date.now().toString().slice(-4);
    return `${cleanName}.${timestamp}`;
  };

  const generatePassword = () => {
    const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789!@#$%^&*';
    let password = '';
    for (let i = 0; i < 12; i++) {
      password += chars.charAt(Math.floor(Math.random() * chars.length));
    }
    form.setValue('password', password);
  };

  const handleFullNameChange = (value: string) => {
    form.setValue('fullName', value);
    if (value && !form.getValues('username')) {
      form.setValue('username', generateUsername(value));
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={handleClose}>
      <DialogContent className="max-w-md max-h-[90vh] overflow-y-auto" data-testid="create-user-modal">
        <DialogHeader>
          <DialogTitle className="flex items-center justify-between">
            <div className="flex items-center">
              <UserPlus className="mr-2 h-5 w-5" />
              Create New User
            </div>
            <Button
              variant="ghost"
              size="icon"
              onClick={handleClose}
              data-testid="button-close-create-user"
            >
              <X className="h-4 w-4" />
            </Button>
          </DialogTitle>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
            <div className="grid grid-cols-1 gap-4">
              <FormField
                control={form.control}
                name="fullName"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Full Name</FormLabel>
                    <FormControl>
                      <Input
                        {...field}
                        onChange={(e) => handleFullNameChange(e.target.value)}
                        placeholder="Enter full name"
                        data-testid="input-full-name"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="username"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Username</FormLabel>
                    <FormControl>
                      <Input
                        {...field}
                        placeholder="Enter username"
                        data-testid="input-username"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="email"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Email</FormLabel>
                    <FormControl>
                      <Input
                        {...field}
                        type="email"
                        placeholder="Enter email address"
                        data-testid="input-email"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="role"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Role</FormLabel>
                    <Select onValueChange={field.onChange} value={field.value}>
                      <FormControl>
                        <SelectTrigger data-testid="select-role">
                          <SelectValue placeholder="Select a role" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        <SelectItem value="hr">HR Personnel</SelectItem>
                        <SelectItem value="accounts">Accounts</SelectItem>
                        <SelectItem value="admin">Admin</SelectItem>
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="password"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="flex items-center justify-between">
                      <span>Temporary Password</span>
                      <Button
                        type="button"
                        variant="ghost"
                        size="sm"
                        onClick={generatePassword}
                        className="text-xs h-auto p-1"
                        data-testid="button-generate-password"
                      >
                        Generate
                      </Button>
                    </FormLabel>
                    <FormControl>
                      <div className="relative">
                        <Input
                          {...field}
                          type={showPassword ? "text" : "password"}
                          placeholder="Enter temporary password"
                          data-testid="input-password"
                        />
                        <Button
                          type="button"
                          variant="ghost"
                          size="icon"
                          className="absolute right-0 top-0 h-full px-3 py-2 hover:bg-transparent"
                          onClick={() => setShowPassword(!showPassword)}
                          data-testid="button-toggle-password"
                        >
                          {showPassword ? (
                            <EyeOff className="h-4 w-4 text-muted-foreground" />
                          ) : (
                            <Eye className="h-4 w-4 text-muted-foreground" />
                          )}
                        </Button>
                      </div>
                    </FormControl>
                    <p className="text-xs text-muted-foreground">
                      User will be asked to change password on first login
                    </p>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <div className="flex justify-end space-x-3 pt-4 border-t border-border">
              <Button
                type="button"
                variant="secondary"
                onClick={handleClose}
                data-testid="button-cancel-create-user"
              >
                Cancel
              </Button>
              <Button
                type="submit"
                disabled={createUserMutation.isPending}
                data-testid="button-submit-create-user"
              >
                {createUserMutation.isPending ? (
                  <>
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-primary-foreground mr-2"></div>
                    Creating...
                  </>
                ) : (
                  <>
                    <UserPlus className="mr-2 h-4 w-4" />
                    Create User
                  </>
                )}
              </Button>
            </div>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/StatusChart.tsx.content:
import { useEffect, useRef } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { PieChart, TrendingUp } from "lucide-react";

interface StatusChartProps {
  data?: Record<string, number>;
  loading?: boolean;
}

export default function StatusChart({ data, loading = false }: StatusChartProps) {
  const chartRef = useRef<HTMLCanvasElement>(null);
  const chartInstanceRef = useRef<any>(null);

  useEffect(() => {
    const loadChart = async () => {
      if (!chartRef.current || loading || !data) return;

      // Dynamically import Chart.js to avoid SSR issues
      const { Chart, registerables } = await import('chart.js');
      Chart.register(...registerables);

      // Destroy existing chart if it exists
      if (chartInstanceRef.current) {
        chartInstanceRef.current.destroy();
      }

      const ctx = chartRef.current.getContext('2d');
      if (!ctx) return;

      // Prepare data for the chart
      const labels = Object.keys(data);
      const values = Object.values(data);

      // Define premium colors for different statuses - more vibrant and professional
      const statusColors: Record<string, string> = {
        new: 'rgb(59, 130, 246)',           // Blue
        scheduled: 'rgb(168, 85, 247)',     // Purple
        completed: 'rgb(34, 197, 94)',      // Green
        not_interested: 'rgb(239, 68, 68)', // Red
        pending: 'rgb(249, 115, 22)',       // Orange
        ready_for_class: 'rgb(16, 185, 129)',   // Teal
        not_available: 'rgb(244, 63, 94)',      // Pink
        no_show: 'rgb(139, 92, 246)',           // Indigo
        reschedule: 'rgb(251, 146, 60)',        // Amber
        pending_but_ready: 'rgb(6, 182, 212)',  // Cyan
        call_back: 'rgb(14, 165, 233)',         // Sky Blue
        wrong_number: 'rgb(236, 72, 153)',      // Rose
      };

      const backgroundColors = labels.map(label => 
        statusColors[label] || 'rgb(107, 114, 128)'
      );

      // Calculate total for statistics
      const total = values.reduce((a, b) => a + b, 0);

      chartInstanceRef.current = new Chart(ctx, {
        type: 'doughnut',
        data: {
          labels: labels.map(label => label.replace('_', ' ').toUpperCase()),
          datasets: [{
            data: values,
            backgroundColor: backgroundColors,
            borderWidth: 3,
            borderColor: 'rgba(255, 255, 255, 0.8)',
            hoverBorderWidth: 4,
            hoverBorderColor: 'rgba(255, 255, 255, 1)',
          }]
        },
        options: {
          responsive: true,
          maintainAspectRatio: false,
          animation: {
            animateRotate: true,
            animateScale: true,
            duration: 1000,
          },
          plugins: {
            legend: {
              position: 'bottom',
              labels: {
                padding: 25,
                usePointStyle: true,
                pointStyle: 'circle',
                font: {
                  size: 12,
                  weight: '600',
                  family: "'Inter', sans-serif",
                },
                color: 'hsl(var(--foreground))',
                generateLabels: (chart) => {
                  const data = chart.data;
                  return (data.labels as string[]).map((label, i) => {
                    const value = (data.datasets[0].data as number[])[i];
                    const percentage = Math.round((value / total) * 100);
                    return {
                      text: `${label} (${value})`,
                      fillStyle: backgroundColors[i],
                      hidden: false,
                      index: i,
                    };
                  });
                }
              }
            },
            tooltip: {
              backgroundColor: 'rgba(0, 0, 0, 0.9)',
              padding: 16,
              titleFont: {
                size: 14,
                weight: 'bold',
              },
              bodyFont: {
                size: 13,
              },
              cornerRadius: 8,
              displayColors: true,
              borderColor: 'rgba(255, 255, 255, 0.1)',
              borderWidth: 1,
              callbacks: {
                label: function(context) {
                  const value = context.parsed;
                  const percentage = Math.round((value / total) * 100);
                  return `  Count: ${value}    ${percentage}%`;
                },
                afterLabel: function(context) {
                  return '';
                }
              }
            },
          },
          elements: {
            arc: {
              borderWidth: 3,
            }
          }
        }
      });
    };

    loadChart();

    // Cleanup function
    return () => {
      if (chartInstanceRef.current) {
        chartInstanceRef.current.destroy();
        chartInstanceRef.current = null;
      }
    };
  }, [data, loading]);

  if (loading) {
    return (
      <Card className="chart-container animate-pulse shadow-lg border border-border">
        <CardHeader className="bg-gradient-to-r from-blue-50 to-purple-50 dark:from-blue-950 dark:to-purple-950 rounded-t-lg">
          <CardTitle className="flex items-center text-lg font-bold">
            <TrendingUp className="mr-2 h-6 w-6 text-blue-600 dark:text-blue-400" />
            Lead Status Distribution
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="h-64 bg-muted rounded"></div>
        </CardContent>
      </Card>
    );
  }

  if (!data || Object.keys(data).length === 0) {
    return (
      <Card className="chart-container shadow-lg border border-border">
        <CardHeader className="bg-gradient-to-r from-blue-50 to-purple-50 dark:from-blue-950 dark:to-purple-950 rounded-t-lg">
          <CardTitle className="flex items-center text-lg font-bold">
            <TrendingUp className="mr-2 h-6 w-6 text-blue-600 dark:text-blue-400" />
            Lead Status Distribution
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="h-64 flex items-center justify-center text-muted-foreground">
            <div className="text-center">
              <PieChart className="h-16 w-16 mx-auto mb-4 opacity-30" />
              <p className="text-lg">No data available</p>
            </div>
          </div>
        </CardContent>
      </Card>
    );
  }

  const total = Object.values(data).reduce((a, b) => a + b, 0);

  return (
    <Card className="chart-container shadow-lg border border-border overflow-hidden" data-testid="status-chart">
      <CardHeader className="bg-gradient-to-r from-blue-50 to-purple-50 dark:from-blue-950 dark:to-purple-950 pb-4">
        <div className="flex items-center justify-between">
          <CardTitle className="flex items-center text-lg font-bold">
            <TrendingUp className="mr-2 h-6 w-6 text-blue-600 dark:text-blue-400" />
            Lead Status Distribution
          </CardTitle>
          <div className="text-right">
            <div className="text-2xl font-bold text-foreground">{total}</div>
            <div className="text-xs text-muted-foreground">Total Leads</div>
          </div>
        </div>
      </CardHeader>
      <CardContent className="pt-6">
        <div className="h-64 relative">
          <canvas 
            ref={chartRef} 
            data-testid="status-chart-canvas"
          ></canvas>
        </div>
      </CardContent>
    </Card>
  );
}

server/index.ts.content:
import "dotenv/config";
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { log } from "./utils";
import { serveStatic } from "./static";
import { storage } from "./storage";
import { hlsStreamer } from "./streaming";
import bcrypt from "bcrypt";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

// Serve HLS segments
app.use('/hls', express.static(hlsStreamer.getHLSDirectory()));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "";
      }

      log(logLine);
    }
  });

  next();
});

// Initialize manager user
async function initializeManagerUser() {
  const managerEmail = process.env.MANAGER_EMAIL;
  const managerPassword = process.env.MANAGER_PASSWORD;

  if (!managerEmail || !managerPassword) {
    console.error("Manager credentials not found in environment variables");
    return;
  }

  try {
    // Check if manager user already exists
    const existingManager = await storage.getUserByEmail(managerEmail);

    if (!existingManager) {
      // Create new manager user
      const hashedPassword = await bcrypt.hash(managerPassword, 10);
      const managerUser = await storage.createUser({
        email: managerEmail,
        firstName: "Manager",
        lastName: "Admin",
        fullName: "Manager Admin",
        passwordHash: hashedPassword,
        role: "manager",
        isActive: true,
      });
      console.log(`Manager user created successfully: ${managerUser.email}`);
    } else {
      // Update existing manager user's password if needed
      const isPasswordValid = await bcrypt.compare(managerPassword, existingManager.passwordHash || '');
      if (!isPasswordValid) {
        const hashedPassword = await bcrypt.hash(managerPassword, 10);
        await storage.updateUser(existingManager.id, {
          passwordHash: hashedPassword,
          role: "manager",
          isActive: true,
        });
        console.log(`Manager user password updated: ${existingManager.email}`);
      } else {
        console.log(`Manager user already exists: ${existingManager.email}`);
      }
    }
  } catch (error) {
    console.error("Error initializing manager user:", error);
  }
}

(async () => {
  // Initialize manager user before starting the server
  await initializeManagerUser();

  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    const { setupVite } = await import("./vite");
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

