import { useState, useEffect } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import {
  UserCheck,
  Search,
  Filter,
  Clock,
  Phone,
  Mail,
  MapPin,
  Edit,
  Save,
  X,
  Trash2,
  Plus,
  ChevronDown,
  ChevronUp,
  Calendar,
  User,
  Building2,
  GraduationCap,
  Timer,
} from "lucide-react";
import { useAuth } from "@/hooks/useAuth";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { insertLeadSchema } from "@shared/schema";
import Sidebar from "@/components/Sidebar";
import FloatingChatbot from "@/components/FloatingChatbot";

// Edit lead form schema - Simplified to handle form data properly
const editLeadSchema = z.object({
  name: z.string().min(1, "Name is required"),
  email: z.string().email("Valid email is required"),
  phone: z.string().optional(),
  location: z.string().optional(),
  degree: z.string().optional(),
  domain: z.string().optional(),
  sessionDays: z
    .enum(["M,W,F", "T,T,S", "daily", "weekend", "custom"])
    .optional()
    .or(z.literal("")),
  walkinDate: z.string().optional().or(z.literal("")),
  walkinTime: z.string().optional().or(z.literal("")),
  timing: z.string().optional(),
  status: z.string().optional(),
  notes: z.string().optional(),
  changeReason: z.string().optional(),
  yearOfPassing: z.string().optional(),
  collegeName: z.string().optional(),
  registrationAmount: z.string().optional(),
  partialAmount: z.string().optional(),
  transactionNumber: z.string().optional(),
  concession: z.string().optional(),
});

// Create lead form schema - Simplified for form submission
const createLeadSchema = z.object({
  name: z.string().min(1, "Name is required"),
  email: z.string().email("Valid email is required"),
  phone: z.string().optional(),
  location: z.string().optional(),
  degree: z.string().optional(),
  domain: z.string().optional(),
  sessionDays: z
    .enum(["M,W,F", "T,T,S", "daily", "weekend", "custom"])
    .optional()
    .or(z.literal("")),
  walkinDate: z.string().optional().or(z.literal("")),
  walkinTime: z.string().optional().or(z.literal("")),
  timing: z.string().optional(),
  notes: z.string().optional(),
});

type EditLeadForm = z.infer<typeof editLeadSchema>;
type CreateLeadForm = z.infer<typeof createLeadSchema>;

export default function MyLeadsPage() {
  const { user } = useAuth();
  const { toast } = useToast();
  const [searchTerm, setSearchTerm] = useState("");
  const [statusFilter, setStatusFilter] = useState("all");
  const [editingLeadId, setEditingLeadId] = useState<number | null>(null);
  const [expandedLeadId, setExpandedLeadId] = useState<number | null>(null);
  const [isCreateModalOpen, setIsCreateModalOpen] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState(() => {
    if (typeof window !== "undefined") {
      return localStorage.getItem("selectedCategory") || "Client Hiring";
    }
    return "Client Hiring";
  });

  useEffect(() => {
    const handleStorageChange = () => {
      const newCategory =
        localStorage.getItem("selectedCategory") || "Client Hiring";
      setSelectedCategory(newCategory);
    };

    window.addEventListener("storage", handleStorageChange);
    return () => window.removeEventListener("storage", handleStorageChange);
  }, []);

  const getStatusColor = (status: string) => {
    switch (status) {
      case "new":
        return "status-new";
      case "register":
        return "status-ready-for-class";
      case "scheduled":
        return "status-scheduled";
      case "completed":
        return "status-completed";
      case "not_interested":
        return "status-not-interested";
      case "pending":
        return "status-pending";
      case "ready_for_class":
        return "status-ready-for-class";
      case "wrong_number":
        return "status-not-interested";
      case "not_picking":
        return "status-not-interested";
      case "call_back":
        return "status-scheduled";
      default:
        return "status-new";
    }
  };

  // Fetch my leads with category filter
  const { data, isLoading, error } = useQuery({
    queryKey: ["/api/my/leads", selectedCategory],
    queryFn: async () => {
      const params = new URLSearchParams();
      params.append("category", selectedCategory);
      const url = `/api/my/leads?${params}`;
      const response = await fetch(url, { credentials: "include" });
      if (!response.ok) throw new Error("Failed to fetch leads");
      return response.json();
    },
    retry: false,
  });

  // Update lead mutation
  const updateLeadMutation = useMutation({
    mutationFn: async ({
      leadId,
      data,
    }: {
      leadId: number;
      data: EditLeadForm;
    }) => {
      const response = await apiRequest("PUT", `/api/leads/${leadId}`, data);
      return response.json();
    },
    onSuccess: (data, variables) => {
      toast({
        title: "Lead Updated",
        description: `Lead updated successfully${variables.data.status === "completed" ? " and forwarded to accounts team" : ""}.`,
      });
      queryClient.invalidateQueries({ queryKey: ["/api/my/leads"] });
      queryClient.invalidateQueries({ queryKey: ["/api/leads"] });
      queryClient.invalidateQueries({ queryKey: ["/api/my/completed"] });
      queryClient.invalidateQueries({ queryKey: ["/api/metrics"] });
      setEditingLeadId(null);
      setExpandedLeadId(null);
    },
    onError: (error) => {
      toast({
        title: "Update Failed",
        description: "Failed to update lead. Please try again.",
        variant: "destructive",
      });
    },
  });

  // Delete lead mutation for HR users
  const deleteLeadMutation = useMutation({
    mutationFn: async (leadId: number) => {
      const response = await apiRequest("DELETE", `/api/leads/${leadId}`);
      return response.json();
    },
    onSuccess: (data) => {
      toast({
        title: "Lead Released",
        description:
          data.message ||
          "Lead has been successfully returned to Lead Management.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/my/leads"] });
      queryClient.invalidateQueries({ queryKey: ["/api/leads"] });
      queryClient.invalidateQueries({ queryKey: ["/api/metrics"] });
    },
    onError: (error: Error) => {
      toast({
        title: "Failed to Delete Lead",
        description:
          error.message || "An error occurred while deleting the lead.",
        variant: "destructive",
      });
    },
  });

  // Create lead mutation for HR users
  const createLeadMutation = useMutation({
    mutationFn: async (data: CreateLeadForm) => {
      const response = await apiRequest("POST", `/api/leads`, data);
      return response.json();
    },
    onSuccess: (data) => {
      toast({
        title: "Lead Created",
        description:
          "New lead has been successfully created and assigned to you.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/my/leads"] });
      queryClient.invalidateQueries({ queryKey: ["/api/leads"] });
      queryClient.invalidateQueries({ queryKey: ["/api/metrics"] });
      setIsCreateModalOpen(false);
      createForm.reset();
    },
    onError: (error: Error) => {
      toast({
        title: "Failed to Create Lead",
        description:
          error.message || "An error occurred while creating the lead.",
        variant: "destructive",
      });
    },
  });

  // Edit form
  const form = useForm<EditLeadForm>({
    resolver: zodResolver(editLeadSchema),
    defaultValues: {
      name: "",
      email: "",
      phone: "",
      location: "",
      degree: "",
      domain: "",
      sessionDays: "",
      walkinDate: "",
      walkinTime: "",
      timing: "",
      status: "new",
      notes: "",
      changeReason: "",
      yearOfPassing: "",
      collegeName: "",
      registrationAmount: "",
      pendingAmount: "",
      partialAmount: "",
      transactionNumber: "",
      concession: "",
    },
  });

  const createForm = useForm<CreateLeadForm>({
    resolver: zodResolver(createLeadSchema),
    defaultValues: {
      name: "",
      email: "",
      phone: "",
      location: "",
      degree: "",
      domain: "",
      sessionDays: "",
      timing: "",
      notes: "",
    },
  });

  const handleEditLead = (lead: any) => {
    setEditingLeadId(lead.id);
    form.reset({
      name: lead.name ?? "",
      email: lead.email ?? "",
      phone: lead.phone ?? "",
      location: lead.location ?? "",
      degree: lead.degree ?? "",
      domain: lead.domain ?? "",
      sessionDays: lead.sessionDays ?? "",
      walkinDate: lead.walkinDate ?? "",
      walkinTime: lead.walkinTime ?? "",
      timing: lead.timing ?? "",
      status: lead.status,
      notes: lead.notes ?? "",
      changeReason: "",
      yearOfPassing: lead.yearOfPassing ?? "",
      collegeName: lead.collegeName ?? "",
      registrationAmount: lead.registrationAmount ?? "",
      pendingAmount: lead.pendingAmount ?? "",
      partialAmount: lead.partialAmount ?? "",
      transactionNumber: lead.transactionNumber ?? "",
      concession: lead.concession ?? "",
    });
  };

  const handleToggleExpand = (leadId: number) => {
    setExpandedLeadId(expandedLeadId === leadId ? null : leadId);
  };

  const handleSubmitEdit = (data: EditLeadForm) => {
    console.log("Form submission - editingLeadId:", editingLeadId);
    console.log("Form data:", data);
    console.log("Form errors:", form.formState.errors);
    if (editingLeadId) {
      updateLeadMutation.mutate({ leadId: editingLeadId, data });
    }
  };

  const handleCancelEdit = () => {
    setEditingLeadId(null);
    form.reset();
  };

  const leads = (data as any)?.leads || [];
  const total = (data as any)?.total || 0;

  // Filter leads based on search and status
  const filteredLeads = leads.filter((lead: any) => {
    const matchesSearch =
      !searchTerm ||
      lead.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      lead.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
      lead.phone.includes(searchTerm);

    const matchesStatus =
      statusFilter === "all" || lead.status === statusFilter;

    return matchesSearch && matchesStatus;
  });

  if (isLoading) {
    return (
      <div className="landing-background min-h-screen flex relative">
        <div className="absolute top-0 left-0 w-64 h-64 bg-gradient-to-br from-[#11754c]/40 to-transparent rounded-full blur-3xl pointer-events-none"></div>
        <div className="absolute top-1/3 right-0 w-80 h-80 bg-gradient-to-br from-[#04e284]/35 to-transparent rounded-full blur-3xl pointer-events-none"></div>
        <div className="absolute bottom-0 left-1/2 transform -translate-x-1/2 w-full h-40 bg-gradient-to-t from-[#11754c]/30 to-transparent skew-y-3 pointer-events-none"></div>
        <Sidebar />
        <div className="flex-1 flex flex-col overflow-hidden relative z-10">
          <div className="p-6">
            <div className="flex items-center space-x-3 mb-6">
              <UserCheck className="w-8 h-8 text-primary" />
              <div>
                <h1 className="text-3xl font-bold">My Leads</h1>
                <p className="text-muted-foreground">
                  Loading your assigned leads...
                </p>
              </div>
            </div>
            <div className="grid gap-4">
              {[1, 2, 3].map((i) => (
                <Card key={i} className="animate-pulse">
                  <CardContent className="p-6">
                    <div className="h-4 bg-muted rounded w-1/3 mb-2"></div>
                    <div className="h-3 bg-muted rounded w-1/2"></div>
                  </CardContent>
                </Card>
              ))}
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    const errorMessage =
      (error as any)?.message || error?.toString() || "Unknown error";
    const isAccessDenied =
      errorMessage.includes("403") ||
      errorMessage.includes("HR users only") ||
      errorMessage.includes("HR and accounts users only");

    return (
      <div className="landing-background min-h-screen flex relative">
        <div className="absolute top-0 left-0 w-64 h-64 bg-gradient-to-br from-[#11754c]/40 to-transparent rounded-full blur-3xl pointer-events-none"></div>
        <div className="absolute top-1/3 right-0 w-80 h-80 bg-gradient-to-br from-[#04e284]/35 to-transparent rounded-full blur-3xl pointer-events-none"></div>
        <div className="absolute bottom-0 left-1/2 transform -translate-x-1/2 w-full h-40 bg-gradient-to-t from-[#11754c]/30 to-transparent skew-y-3 pointer-events-none"></div>
        <Sidebar />
        <div className="flex-1 flex flex-col overflow-hidden relative z-10">
          <div className="p-6">
            <div className="flex items-center space-x-3 mb-6">
              <UserCheck className="w-8 h-8 text-primary" />
              <div>
                <h1 className="text-3xl font-bold">My Leads</h1>
                <p className="text-muted-foreground">Your assigned leads</p>
              </div>
            </div>
            <Card>
              <CardContent className="p-6 text-center">
                {isAccessDenied ? (
                  <div className="space-y-2">
                    <h3 className="text-lg font-semibold text-destructive">
                      Access Denied
                    </h3>
                    <p className="text-muted-foreground">
                      This page is only available to HR and accounts users.
                      Please contact your manager if you believe this is an
                      error.
                    </p>
                  </div>
                ) : (
                  <div className="space-y-2">
                    <h3 className="text-lg font-semibold">
                      Failed to Load Leads
                    </h3>
                    <p className="text-muted-foreground">
                      Unable to load your leads at this time. Please try again
                      or contact support if the problem persists.
                    </p>
                  </div>
                )}
              </CardContent>
            </Card>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="landing-background min-h-screen flex relative">
      {/* Decorative shapes */}
      <div className="absolute top-0 left-0 w-64 h-64 bg-gradient-to-br from-[#11754c]/40 to-transparent rounded-full blur-3xl pointer-events-none"></div>
      <div className="absolute top-1/3 right-0 w-80 h-80 bg-gradient-to-br from-[#04e284]/35 to-transparent rounded-full blur-3xl pointer-events-none"></div>
      <div className="absolute bottom-0 left-1/2 transform -translate-x-1/2 w-full h-40 bg-gradient-to-t from-[#11754c]/30 to-transparent skew-y-3 pointer-events-none"></div>

      <Sidebar />

      {/* Main Content */}
      <div className="flex-1 flex flex-col overflow-hidden relative z-10">
        <main className="flex-1 overflow-y-auto p-6">
          <div className="space-y-6" data-testid="page-my-leads">
            {/* Header */}
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-3">
                <UserCheck className="w-8 h-8 text-primary" />
                <div>
                  <h1
                    className="text-3xl font-bold"
                    data-testid="heading-my-leads"
                  >
                    My Leads
                  </h1>
                  <p className="text-muted-foreground">
                    {total > 0
                      ? `${total} lead${total !== 1 ? "s" : ""} assigned to you`
                      : "No leads assigned yet"}
                  </p>
                </div>
              </div>
              <div className="flex items-center space-x-3">
                {/* Create Lead button - only for HR users */}
                {user?.role === "hr" && (
                  <Button
                    onClick={() => setIsCreateModalOpen(true)}
                    className="flex items-center space-x-2"
                    data-testid="button-create-lead"
                  >
                    <Plus className="w-4 h-4" />
                    <span>Create Lead</span>
                  </Button>
                )}
                {total > 0 && (
                  <Badge
                    variant="outline"
                    className="text-lg px-3 py-1"
                    data-testid="badge-total-count"
                  >
                    {total} Total
                  </Badge>
                )}
              </div>
            </div>

            {/* Filters */}
            {total > 0 && (
              <div className="flex flex-col sm:flex-row gap-4">
                <div className="flex-1 relative">
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground w-4 h-4" />
                  <Input
                    placeholder="Search leads by name, email, or phone..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="pl-10"
                    data-testid="input-search-leads"
                  />
                </div>
                <Select value={statusFilter} onValueChange={setStatusFilter}>
                  <SelectTrigger
                    className="w-full sm:w-48"
                    data-testid="select-status-filter"
                  >
                    <Filter className="w-4 h-4 mr-2" />
                    <SelectValue placeholder="Filter by status" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Status</SelectItem>
                    <SelectItem value="new">New</SelectItem>
                    <SelectItem value="register">Register</SelectItem>
                    <SelectItem value="scheduled">Scheduled</SelectItem>
                    <SelectItem value="completed">Completed</SelectItem>
                    <SelectItem value="not_interested">
                      Not Interested
                    </SelectItem>
                    <SelectItem value="pending">Pending</SelectItem>
                    <SelectItem value="ready_for_class">
                      Ready for Class
                    </SelectItem>
                  </SelectContent>
                </Select>
              </div>
            )}

            {/* Leads Grid */}
            {filteredLeads.length > 0 ? (
              <div className="grid gap-4">
                {filteredLeads.map((lead: any) => (
                  <Card
                    key={lead.id}
                    className="shadow-green-md hover:shadow-green-bright transition-shadow"
                    data-testid={`card-lead-${lead.id}`}
                  >
                    <CardHeader
                      className="pb-3 cursor-pointer"
                      onClick={() => handleToggleExpand(lead.id)}
                    >
                      <div className="flex items-start justify-between">
                        <div className="space-y-1 flex-1">
                          <div className="flex items-center space-x-2">
                            <CardTitle
                              className="text-lg"
                              data-testid={`text-lead-name-${lead.id}`}
                            >
                              {lead.name}
                            </CardTitle>
                            {expandedLeadId === lead.id ? (
                              <ChevronUp className="w-4 h-4 text-muted-foreground" />
                            ) : (
                              <ChevronDown className="w-4 h-4 text-muted-foreground" />
                            )}
                          </div>
                          <CardDescription className="flex items-center space-x-2">
                            <span data-testid={`text-lead-email-${lead.id}`}>
                              {lead.email}
                            </span>
                            {lead.degree && (
                              <>
                                <span></span>
                                <span
                                  className="text-xs bg-muted px-2 py-1 rounded"
                                  data-testid={`badge-lead-degree-${lead.id}`}
                                >
                                  {lead.degree}
                                </span>
                              </>
                            )}
                          </CardDescription>
                        </div>
                        <div
                          className="flex items-center space-x-2"
                          onClick={(e) => e.stopPropagation()}
                        >
                          <Badge
                            className={`status-badge ${getStatusColor(lead.status)}`}
                            data-testid={`status-${lead.id}`}
                          >
                            {lead.status.replace("_", " ")}
                          </Badge>
                          {lead.createdAt && (
                            <div className="flex items-center text-xs text-muted-foreground">
                              <Clock className="w-3 h-3 mr-1" />
                              <span>
                                Added{" "}
                                {new Date(lead.createdAt).toLocaleDateString()}
                              </span>
                            </div>
                          )}
                          <Button
                            size="sm"
                            variant="outline"
                            onClick={(e) => {
                              e.stopPropagation();
                              handleEditLead(lead);
                            }}
                            data-testid={`button-edit-${lead.id}`}
                          >
                            <Edit className="w-3 h-3 mr-1" />
                            Edit
                          </Button>
                          {/* Delete button - only for HR users */}
                          {user?.role === "hr" && (
                            <Button
