                      />
                    </div>
                    <div className="grid grid-cols-2 gap-4">
                      <FormField
                        control={createForm.control}
                        name="sessionDays"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Session Days</FormLabel>
                            <Select
                              onValueChange={field.onChange}
                              defaultValue={field.value}
                            >
                              <FormControl>
                                <SelectTrigger data-testid="select-create-session-days">
                                  <SelectValue placeholder="Select session pattern" />
                                </SelectTrigger>
                              </FormControl>
                              <SelectContent>
                                <SelectItem value="M,W,F">
                                  M,W,F (Monday, Wednesday, Friday)
                                </SelectItem>
                                <SelectItem value="T,T,S">
                                  T,T,S (Tuesday, Thursday, Saturday)
                                </SelectItem>
                                <SelectItem value="daily">
                                  Daily (Monday to Saturday)
                                </SelectItem>
                                <SelectItem value="weekend">
                                  Weekend Only
                                </SelectItem>
                                <SelectItem value="custom">
                                  Custom Schedule
                                </SelectItem>
                              </SelectContent>
                            </Select>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <FormField
                        control={createForm.control}
                        name="timing"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Timing</FormLabel>
                            <FormControl>
                              <Input
                                placeholder="e.g., 9:00 AM - 5:00 PM"
                                {...field}
                                data-testid="input-create-timing"
                              />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                    </div>
                    <FormField
                      control={createForm.control}
                      name="notes"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Notes</FormLabel>
                          <FormControl>
                            <Textarea
                              placeholder="Add any initial notes about this lead..."
                              rows={3}
                              {...field}
                              data-testid="input-create-notes"
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                    <DialogFooter>
                      <Button
                        type="button"
                        variant="outline"
                        onClick={() => setIsCreateModalOpen(false)}
                        data-testid="button-cancel-create"
                      >
                        <X className="w-4 h-4 mr-2" />
                        Cancel
                      </Button>
                      <Button
                        type="submit"
                        disabled={createLeadMutation.isPending}
                        data-testid="button-save-create"
                      >
                        <Plus className="w-4 h-4 mr-2" />
                        {createLeadMutation.isPending
                          ? "Creating..."
                          : "Create Lead"}
                      </Button>
                    </DialogFooter>
                  </form>
                </Form>
              </DialogContent>
            </Dialog>
            <FloatingChatbot />
          </div>
        </main>
      </div>
    </div>
  );
}

