import { useState } from 'react';
import { useQuery } from '@tanstack/react-query';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Skeleton } from '@/components/ui/skeleton';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { 
  BarChart, 
  Bar, 
  XAxis, 
  YAxis, 
  CartesianGrid, 
  Tooltip, 
  Legend, 
  ResponsiveContainer,
  PieChart,
  Pie,
  Cell
} from 'recharts';
import { 
  MousePointer2, 
  Keyboard, 
  Timer, 
  ExternalLink, 
  AlertTriangle,
  User,
  Eye,
  TrendingUp
} from 'lucide-react';
import Sidebar from '@/components/Sidebar';
import { useAuth } from '@/hooks/useAuth';

type ProductivitySummary = {
  userId: string;
  userName: string;
  mouseIdleWarnings: number;
  keyboardIdleWarnings: number;
  longKeyPressWarnings: number;
  tabSwitches: number;
};

type ProductivityStats = {
  mouseIdleWarnings: number;
  keyboardIdleWarnings: number;
  longKeyPressWarnings: number;
  tabSwitches: number;
  tabSwitchUrls: Array<{ url: string; count: number; lastVisit: string }>;
};

type ProductivityEvent = {
  id: number;
  userId: string;
  eventType: string;
  metadata: any;
  createdAt: string;
};

type UserProductivity = {
  userId: string;
  userName: string;
  stats: ProductivityStats;
  events: ProductivityEvent[];
};

const COLORS = ['#ef4444', '#f97316', '#eab308', '#22c55e'];

export default function Productivity() {
  const { user } = useAuth();
  const [selectedUser, setSelectedUser] = useState<string | null>(null);
  const [detailsOpen, setDetailsOpen] = useState(false);

  const { data: summaryData, isLoading: summaryLoading } = useQuery<ProductivitySummary[]>({
    queryKey: ['/api/productivity/summary'],
    enabled: (user as any)?.role === 'manager',
  });

  const { data: userDetails, isLoading: detailsLoading } = useQuery<UserProductivity>({
    queryKey: ['/api/productivity/user', selectedUser],
    queryFn: async () => {
      const response = await fetch(`/api/productivity/user/${selectedUser}`, {
        credentials: 'include'
      });
      if (!response.ok) throw new Error('Failed to fetch');
      return response.json();
    },
    enabled: !!selectedUser && detailsOpen,
  });

  const handleViewDetails = (userId: string) => {
    setSelectedUser(userId);
    setDetailsOpen(true);
  };

  const getTotalWarnings = (user: ProductivitySummary) => {
    return user.mouseIdleWarnings + user.keyboardIdleWarnings + user.longKeyPressWarnings;
  };

  // Productivity Score Formula: 100 - (InactivityCount * 2 + TabSwitchCount * 3 + KeyboardWarnings * 1.5)
  const calculateProductivityScore = (user: ProductivitySummary): number => {
    const inactivityCount = user.mouseIdleWarnings;
    const tabSwitchCount = user.tabSwitches;
    const keyboardWarnings = user.keyboardIdleWarnings + user.longKeyPressWarnings;

    const score = 100 - (inactivityCount * 2 + tabSwitchCount * 3 + keyboardWarnings * 1.5);
    return Math.max(0, Math.min(100, Math.round(score))); // Clamp between 0 and 100
  };

  const getScoreColor = (score: number): { label: string; bgColor: string; textColor: string } => {
    if (score >= 80) return { label: 'Excellent', bgColor: 'bg-green-500', textColor: 'text-green-500' };
    if (score >= 50) return { label: 'Good', bgColor: 'bg-yellow-500', textColor: 'text-yellow-500' };
    return { label: 'Needs Improvement', bgColor: 'bg-red-500', textColor: 'text-red-500' };
  };

  const getWarningLevel = (total: number): { label: string; color: string } => {
    if (total === 0) return { label: 'Excellent', color: 'bg-green-500' };
    if (total <= 5) return { label: 'Good', color: 'bg-yellow-500' };
    if (total <= 15) return { label: 'Needs Attention', color: 'bg-orange-500' };
    return { label: 'Critical', color: 'bg-red-500' };
  };

  const chartData = summaryData?.map(user => ({
    name: user.userName.split(' ')[0],
    'Mouse Idle': user.mouseIdleWarnings,
    'Keyboard Idle': user.keyboardIdleWarnings,
    'Long Key Press': user.longKeyPressWarnings,
    'Tab Switches': user.tabSwitches,
  })) || [];

  const pieData = [
    { name: 'Mouse Idle', value: summaryData?.reduce((acc, u) => acc + u.mouseIdleWarnings, 0) || 0 },
    { name: 'Keyboard Idle', value: summaryData?.reduce((acc, u) => acc + u.keyboardIdleWarnings, 0) || 0 },
    { name: 'Long Key Press', value: summaryData?.reduce((acc, u) => acc + u.longKeyPressWarnings, 0) || 0 },
    { name: 'Tab Switches', value: summaryData?.reduce((acc, u) => acc + u.tabSwitches, 0) || 0 },
  ];

  const totalStats = {
    totalWarnings: summaryData?.reduce((acc, u) => acc + getTotalWarnings(u), 0) || 0,
    totalTabSwitches: summaryData?.reduce((acc, u) => acc + u.tabSwitches, 0) || 0,
    hrCount: summaryData?.length || 0,
  };

  if ((user as any)?.role !== 'manager') {
    return (
      <div className="flex h-screen">
        <Sidebar />
        <main className="flex-1 overflow-auto p-8">
          <div className="text-center py-12">
            <AlertTriangle className="h-12 w-12 text-yellow-500 mx-auto mb-4" />
            <h2 className="text-xl font-semibold">Access Denied</h2>
            <p className="text-muted-foreground">Only managers can view productivity data.</p>
          </div>
        </main>
      </div>
    );
  }

  return (
    <div className="flex h-screen">
      <Sidebar />
      <main className="flex-1 overflow-auto">
        <div className="p-8">
          <div className="mb-8">
            <h1 className="text-3xl font-bold flex items-center gap-2">
              <TrendingUp className="h-8 w-8 text-primary" />
              HR Productivity Monitor
            </h1>
            <p className="text-muted-foreground mt-2">
              Track and analyze HR team activity and engagement
            </p>
          </div>

          <div className="grid gap-4 md:grid-cols-3 mb-8">
            <Card>
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium">Total Warnings</CardTitle>
                <AlertTriangle className="h-4 w-4 text-yellow-500" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold">{totalStats.totalWarnings}</div>
                <p className="text-xs text-muted-foreground">
                  Combined idle and long press warnings
                </p>
              </CardContent>
            </Card>
            <Card>
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium">Tab Switches</CardTitle>
                <ExternalLink className="h-4 w-4 text-blue-500" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold">{totalStats.totalTabSwitches}</div>
                <p className="text-xs text-muted-foreground">
                  Total tab/window switches tracked
                </p>
              </CardContent>
            </Card>
            <Card>
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium">Active HR Users</CardTitle>
                <User className="h-4 w-4 text-green-500" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold">{totalStats.hrCount}</div>
                <p className="text-xs text-muted-foreground">
                  HR team members being monitored
                </p>
              </CardContent>
            </Card>
          </div>

          <div className="grid gap-6 md:grid-cols-2 mb-8">
            <Card>
              <CardHeader>
                <CardTitle>Warnings by HR User</CardTitle>
                <CardDescription>Breakdown of warning types per team member</CardDescription>
              </CardHeader>
              <CardContent>
                {summaryLoading ? (
                  <Skeleton className="h-[300px] w-full" />
                ) : chartData.length > 0 ? (
                  <ResponsiveContainer width="100%" height={300}>
                    <BarChart data={chartData}>
                      <CartesianGrid strokeDasharray="3 3" />
                      <XAxis dataKey="name" />
                      <YAxis />
                      <Tooltip />
                      <Legend />
                      <Bar dataKey="Mouse Idle" fill="#ef4444" />
                      <Bar dataKey="Keyboard Idle" fill="#f97316" />
                      <Bar dataKey="Long Key Press" fill="#eab308" />
                    </BarChart>
                  </ResponsiveContainer>
                ) : (
                  <div className="h-[300px] flex items-center justify-center text-muted-foreground">
                    No productivity data available yet
                  </div>
                )}
              </CardContent>
            </Card>

            <Card>
              <CardHeader>
                <CardTitle>Warning Distribution</CardTitle>
                <CardDescription>Overall distribution of warning types</CardDescription>
              </CardHeader>
              <CardContent>
                {summaryLoading ? (
                  <Skeleton className="h-[300px] w-full" />
                ) : pieData.some(d => d.value > 0) ? (
                  <ResponsiveContainer width="100%" height={300}>
                    <PieChart>
                      <Pie
                        data={pieData}
                        cx="50%"
                        cy="50%"
                        labelLine={false}
                        label={({ name, percent }) => `${name} ${(percent * 100).toFixed(0)}%`}
                        outerRadius={100}
                        fill="#8884d8"
                        dataKey="value"
                      >
                        {pieData.map((entry, index) => (
                          <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                        ))}
                      </Pie>
                      <Tooltip />
                    </PieChart>
                  </ResponsiveContainer>
                ) : (
                  <div className="h-[300px] flex items-center justify-center text-muted-foreground">
                    No warnings recorded yet
                  </div>
                )}
              </CardContent>
            </Card>
          </div>

          <Card>
            <CardHeader>
              <CardTitle>HR Team Productivity</CardTitle>
              <CardDescription>Click on a row to view detailed activity</CardDescription>
            </CardHeader>
            <CardContent>
              {summaryLoading ? (
                <div className="space-y-2">
                  {[1, 2, 3].map(i => (
                    <Skeleton key={i} className="h-12 w-full" />
                  ))}
                </div>
              ) : summaryData && summaryData.length > 0 ? (
                <Table>
                  <TableHeader>
                    <TableRow>
                      <TableHead>HR Name</TableHead>
                      <TableHead className="text-center">
                        <div className="flex items-center justify-center gap-1">
                          <MousePointer2 className="h-4 w-4" />
                          Mouse Idle
                        </div>
                      </TableHead>
                      <TableHead className="text-center">
                        <div className="flex items-center justify-center gap-1">
                          <Keyboard className="h-4 w-4" />
                          Keyboard Idle
                        </div>
                      </TableHead>
                      <TableHead className="text-center">
                        <div className="flex items-center justify-center gap-1">
                          <Timer className="h-4 w-4" />
                          Long Press
                        </div>
                      </TableHead>
                      <TableHead className="text-center">
                        <div className="flex items-center justify-center gap-1">
                          <ExternalLink className="h-4 w-4" />
                          Tab Switches
                        </div>
                      </TableHead>
                      <TableHead className="text-center">Productivity Score</TableHead>
                      <TableHead className="text-center">Actions</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {summaryData.map((hrUser) => {
                      const score = calculateProductivityScore(hrUser);
                      const scoreLevel = getScoreColor(score);
                      return (
                        <TableRow key={hrUser.userId} className="cursor-pointer hover:bg-muted/50">
                          <TableCell className="font-medium">{hrUser.userName}</TableCell>
                          <TableCell className="text-center">{hrUser.mouseIdleWarnings}</TableCell>
                          <TableCell className="text-center">{hrUser.keyboardIdleWarnings}</TableCell>
                          <TableCell className="text-center">{hrUser.longKeyPressWarnings}</TableCell>
                          <TableCell className="text-center">{hrUser.tabSwitches}</TableCell>
                          <TableCell className="text-center">
                            <div className="flex flex-col items-center gap-1">
                              <div className={`text-lg font-bold ${scoreLevel.textColor}`}>
                                {score}
                              </div>
                              <Badge className={scoreLevel.bgColor}>{scoreLevel.label}</Badge>
                            </div>
                          </TableCell>
                          <TableCell className="text-center">
                            <Button 
                              variant="ghost" 
                              size="sm"
                              onClick={() => handleViewDetails(hrUser.userId)}
                            >
                              <Eye className="h-4 w-4 mr-1" />
                              Details
                            </Button>
                          </TableCell>
                        </TableRow>
                      );
                    })}
                  </TableBody>
                </Table>
              ) : (
                <div className="text-center py-8 text-muted-foreground">
                  <User className="h-12 w-12 mx-auto mb-4 opacity-50" />
                  <p>No HR users found or no activity tracked yet</p>
                </div>
              )}
            </CardContent>
          </Card>
        </div>
      </main>

      <Dialog open={detailsOpen} onOpenChange={setDetailsOpen}>
        <DialogContent className="max-w-4xl max-h-[80vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle className="flex items-center gap-2">
              <User className="h-5 w-5" />
              {userDetails?.userName || 'Loading...'} - Activity Details
            </DialogTitle>
            <DialogDescription>
              Detailed productivity metrics and browsing activity
            </DialogDescription>
          </DialogHeader>

          {detailsLoading ? (
            <div className="space-y-4">
              <Skeleton className="h-24 w-full" />
              <Skeleton className="h-48 w-full" />
            </div>
          ) : userDetails ? (
            <div className="space-y-6">
              <div className="grid grid-cols-4 gap-4">
                <Card>
                  <CardContent className="pt-4">
                    <div className="flex items-center gap-2">
                      <MousePointer2 className="h-5 w-5 text-red-500" />
                      <div>
                        <p className="text-2xl font-bold">{userDetails.stats.mouseIdleWarnings}</p>
                        <p className="text-xs text-muted-foreground">Mouse Idle</p>
                      </div>
                    </div>
                  </CardContent>
                </Card>
                <Card>
                  <CardContent className="pt-4">
                    <div className="flex items-center gap-2">
                      <Keyboard className="h-5 w-5 text-orange-500" />
                      <div>
                        <p className="text-2xl font-bold">{userDetails.stats.keyboardIdleWarnings}</p>
                        <p className="text-xs text-muted-foreground">Keyboard Idle</p>
                      </div>
                    </div>
                  </CardContent>
                </Card>
                <Card>
                  <CardContent className="pt-4">
                    <div className="flex items-center gap-2">
                      <Timer className="h-5 w-5 text-yellow-500" />
                      <div>
                        <p className="text-2xl font-bold">{userDetails.stats.longKeyPressWarnings}</p>
                        <p className="text-xs text-muted-foreground">Long Press</p>
                      </div>
                    </div>
                  </CardContent>
                </Card>
                <Card>
                  <CardContent className="pt-4">
                    <div className="flex items-center gap-2">
                      <ExternalLink className="h-5 w-5 text-blue-500" />
                      <div>
                        <p className="text-2xl font-bold">{userDetails.stats.tabSwitches}</p>
                        <p className="text-xs text-muted-foreground">Tab Switches</p>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              </div>

              {userDetails.stats.tabSwitchUrls.length > 0 && (
                <Card>
                  <CardHeader>
                    <CardTitle className="text-lg">Browsing Activity</CardTitle>
                    <CardDescription>URLs visited when switching away from the app</CardDescription>
                  </CardHeader>
                  <CardContent>
                    <Table>
                      <TableHeader>
                        <TableRow>
                          <TableHead>URL / Activity</TableHead>
                          <TableHead className="text-center">Count</TableHead>
                          <TableHead className="text-right">Last Visit</TableHead>
                        </TableRow>
                      </TableHeader>
                      <TableBody>
                        {userDetails.stats.tabSwitchUrls.map((item, index) => (
                          <TableRow key={index}>
                            <TableCell className="max-w-md truncate">
                              {item.url}
                            </TableCell>
                            <TableCell className="text-center">{item.count}</TableCell>
                            <TableCell className="text-right text-muted-foreground">
                              {new Date(item.lastVisit).toLocaleString()}
                            </TableCell>
                          </TableRow>
                        ))}
                      </TableBody>
                    </Table>
                  </CardContent>
                </Card>
              )}

              <Card>
                <CardHeader>
                  <CardTitle className="text-lg">Recent Events</CardTitle>
                  <CardDescription>Last 20 productivity events</CardDescription>
                </CardHeader>
                <CardContent>
                  {userDetails.events.length > 0 ? (
                    <Table>
                      <TableHeader>
                        <TableRow>
                          <TableHead>Event Type</TableHead>
                          <TableHead>Details</TableHead>
                          <TableHead className="text-right">Time</TableHead>
                        </TableRow>
                      </TableHeader>
                      <TableBody>
                        {userDetails.events.slice(0, 20).map((event) => (
                          <TableRow key={event.id}>
                            <TableCell>
                              <Badge variant="outline">
                                {event.eventType.replace(/([A-Z])/g, ' $1').trim()}
                              </Badge>
                            </TableCell>
                            <TableCell className="max-w-xs truncate">
                              {event.metadata?.url || event.metadata?.key || '-'}
                            </TableCell>
                            <TableCell className="text-right text-muted-foreground">
                              {new Date(event.createdAt).toLocaleString()}
                            </TableCell>
                          </TableRow>
                        ))}
                      </TableBody>
                    </Table>
                  ) : (
                    <p className="text-center py-4 text-muted-foreground">No events recorded</p>
                  )}
                </CardContent>
              </Card>
            </div>
          ) : null}
        </DialogContent>
      </Dialog>
    </div>
  );
}

